SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE cih_enviar_email_pos_alta ( cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_atendimento_w	bigint;
ds_email_remetente_w	varchar(100);
ds_email_destino_w	varchar(100);
ds_assunto_w		varchar(100);
ds_mensagem_w		varchar(4000);
ds_texto_w		varchar(6000);
nm_pessoa_fisica_w	varchar(255);
cd_pessoa_fisica_w	varchar(10);
ie_envio_w		varchar(1);
ds_procedimento_w		procedimento.ds_procedimento%TYPE;
dt_cirurgia_w		timestamp;
					
C01 CURSOR FOR 
	SELECT	a.cd_pessoa_fisica, 
		substr(obter_nome_pf(a.cd_pessoa_fisica),1,255) nm_pessoa_fisica, 
		a.nr_atendimento, 
		substr(obter_exame_agenda(b.cd_procedimento_princ, b.ie_origem_proced, b.nr_seq_proc_interno),1,240) ds_procedimento, 
		b.dt_inicio_real 
	from	atendimento_paciente a, 
		cirurgia b 
	where	a.nr_atendimento = b.nr_atendimento 
	and	a.cd_estabelecimento = cd_estabelecimento_p 
	and	(a.dt_alta IS NOT NULL AND a.dt_alta::text <> '') 
	and	Cih_Obter_Regra_Acomp_Pos_Alta(a.nr_atendimento,b.cd_procedimento_princ,b.ie_origem_proced,cd_estabelecimento_p) = 'S' 
	order by 2;
			

BEGIN 
 
select	max(ds_remetente_email), 
	max(ds_assunto), 
	max(ds_mensagem) 
into STRICT	ds_email_remetente_w, 
	ds_assunto_w, 
	ds_mensagem_w 
from	cih_parametros 
where (cd_estabelecimento = cd_estabelecimento_p 
or (coalesce(cd_estabelecimento::text, '') = '' 
and not exists (SELECT	1 
		from	cih_parametros 
		where	cd_estabelecimento = cd_estabelecimento_p)));
 
open C01;
loop 
fetch C01 into	 
	cd_pessoa_fisica_w, 
	nm_pessoa_fisica_w, 
	nr_atendimento_w, 
	ds_procedimento_w, 
	dt_cirurgia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	ds_texto_w := Wheb_mensagem_pck.get_texto(306079,'NM_PESSOA_FISICA_W='||nm_pessoa_fisica_w||';DS_PROCEDIMENTO_W='||ds_procedimento_w||';DT_CIRURGIA_W='||to_char(dt_cirurgia_w,'dd/mm/yyyy'))||chr(13)||chr(10)||chr(13)||chr(10)||ds_mensagem_w;
	ds_email_destino_w := obter_email_medicos_cirurgia(nr_atendimento_w,cd_estabelecimento_p);
	 
	select 	CASE WHEN obter_se_alta_obito(nr_atendimento_w)='S' THEN 'N' WHEN obter_se_alta_obito(nr_atendimento_w)='N' THEN 'S' END  
	into STRICT	ie_envio_w 
	;
	 
	if (ds_email_destino_w IS NOT NULL AND ds_email_destino_w::text <> '') then 
		if (ie_envio_w = 'S') then 
			CALL Enviar_Email(ds_assunto_w,ds_texto_w,ds_email_remetente_w,ds_email_destino_w,nm_usuario_p,'M');
		end if;
		 
		CALL Gerar_log_envio_pos_alta(cd_pessoa_fisica_w, nr_atendimento_w, '', ds_email_destino_w, nm_usuario_p);
	end if;
	end;
end loop;
close C01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
