CREATE OR REPLACE PROCEDURE cih_gerar_cirurgias_envio ( nr_seq_medico_envio_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
dt_inicial_w	timestamp;
dt_final_w	timestamp;
cd_medico_w	varchar(10);
nr_cirurgia_w	bigint;

C01 CURSOR FOR 
	SELECT	b.nr_cirurgia 
	from	atendimento_paciente a, 
		cirurgia b 
	where	a.nr_atendimento = b.nr_atendimento 
	and	(a.dt_alta IS NOT NULL AND a.dt_alta::text <> '') 
	and 	b.ie_status_cirurgia = '2' 
	and	b.cd_medico_cirurgiao = cd_medico_w 
	--and	obter_se_alta_obito(a.nr_atendimento) = 'N' 
	and	b.dt_inicio_real between inicio_dia(dt_inicial_w) and fim_dia(dt_final_w) 
	and not exists (SELECT	1 
			from	cih_medico_envio_item c 
			where	c.nr_seq_medico_envio = nr_seq_medico_envio_p 
			and	c.nr_cirurgia = b.nr_cirurgia) 
	order by 1;
			

BEGIN 
 
if (nr_seq_medico_envio_p IS NOT NULL AND nr_seq_medico_envio_p::text <> '') then 
 
	select	dt_inicial, 
		dt_final, 
		cd_medico 
	into STRICT	dt_inicial_w, 
		dt_final_w, 
		cd_medico_w 
	from	cih_medico_envio 
	where	nr_sequencia = nr_seq_medico_envio_p;
 
	open C01;
	loop 
	fetch C01 into	 
		nr_cirurgia_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
		 
		insert into cih_medico_envio_item( 
			nr_sequencia, 
			dt_atualizacao, 
			nm_usuario, 
			dt_atualizacao_nrec, 
			nm_usuario_nrec, 
			nr_cirurgia, 
			nr_seq_medico_envio) 
		values (	nextval('cih_medico_envio_item_seq'), 
			clock_timestamp(), 
			nm_usuario_p, 
			clock_timestamp(), 
			nm_usuario_p, 
			nr_cirurgia_w, 
			nr_seq_medico_envio_p);
			 
		end;
	end loop;
	close C01;
	 
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE cih_gerar_cirurgias_envio ( nr_seq_medico_envio_p bigint, nm_usuario_p text) FROM PUBLIC;

