CREATE OR REPLACE PROCEDURE cih_gerar_precaucao ( nr_atendimento_p bigint, cd_precaucao_p bigint, cd_motivo_p bigint, cd_origem_p bigint, ds_observacao_p text, nm_usuario_p text) AS $body$
DECLARE

 
 
ds_motivo_w		varchar(255);
nr_sequencia_w	atendimento_precaucao.nr_sequencia%type;


BEGIN 
 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then 
	begin 
	select	nextval('atendimento_precaucao_seq') 
	into STRICT	nr_sequencia_w 
	;
 
	insert into atendimento_precaucao(	nr_sequencia, 
						dt_atualizacao, 
						nm_usuario, 
						nm_usuario_nrec, 
						dt_atualizacao_nrec, 
						nr_seq_precaucao, 
						nr_atendimento, 
						nr_seq_motivo_isol, 
						ds_observacao, 
						dt_liberacao, 
						ds_justificativa_analise, 
						dt_analise, 
						nm_usuario_analise, 
						dt_registro, 
						nr_seq_origem, 
						dt_inicio, 
						ie_analise, 
						ie_situacao 
						) 
	values (	nr_sequencia_w, 
						clock_timestamp(), 
						nm_usuario_p, 
						nm_usuario_p, 
						clock_timestamp(), 
						cd_precaucao_p, 
						nr_atendimento_p, 
						cd_motivo_p, 
						ds_observacao_p, 
						clock_timestamp(), 
						ds_observacao_p, 
						clock_timestamp(), 
						nm_usuario_p, 
						clock_timestamp(), 
						cd_origem_p, 
						clock_timestamp(), 
						'S', 
						'A');
	 
	update	atendimento_paciente 
	set		ie_paciente_isolado = 'S' 
	where	nr_atendimento = nr_atendimento_p;
	 
	if (cd_motivo_p IS NOT NULL AND cd_motivo_p::text <> '') then 
		select 	max(substr(obter_motivo_precaucao(nr_sequencia_w),1,255)) 
		into STRICT	ds_motivo_w 
		;
 
		CALL gerar_motivo_isolamento(nr_atendimento_p,cd_motivo_p,nm_usuario_p,'I',ds_motivo_w,null,null,clock_timestamp(),null,nr_sequencia_w,nr_sequencia_w);
	end if;
	CALL gerar_lancamento_automatico(nr_atendimento_p, null, 559, nm_usuario_p, null, null, null, null, null, null);
	 
	end;
end if;
 
commit;
 
end;
$body$
