CREATE OR REPLACE PROCEDURE cih_gravar_data_envio_email ( nr_seq_medico_envio_p bigint, nm_usuario_p text) AS $body$
BEGIN

if (nr_seq_medico_envio_p IS NOT NULL AND nr_seq_medico_envio_p::text <> '') then

	update	cih_medico_envio
	set	dt_envio_email 	= clock_timestamp(),
		dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p
	where	nr_sequencia	= nr_seq_medico_envio_p;

end if;

commit;

end;
$body$
