SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE cih_importar_procedimentos ( nr_ficha_ocorrencia_p bigint, nm_usuario_p text) AS $body$
DECLARE

cd_procedimento_w	bigint;
dt_inicio_procedimento_w	timestamp;
dt_final_procedimento_w	timestamp;
cd_setor_atendimento_w	integer;
nr_atendimento_w		bigint;
cd_clinica_w		bigint;
nr_seq_procedimento_w	bigint;
nr_seq_disp_pac_proc_w	bigint;
c01 CURSOR FOR
	SELECT 	c.cd_procedimento,
		coalesce(b.dt_inicio_procedimento,b.dt_procedimento),
		b.dt_final_procedimento,
		b.cd_setor_atendimento,
		null nr_sequencia
	from 	procedimento a,
		procedimento_paciente b,
		cih_procedimento c
	where	a.cd_procedimento 	= b.cd_procedimento
	and	a.ie_origem_proced	= b.ie_origem_proced
	and	a.cd_proc_cih		= c.cd_procedimento
	and	b.nr_atendimento 	= nr_atendimento_w
	and	not exists (SELECT	1
			from	cih_proc_realizado x
			where	x.nr_ficha_ocorrencia  	= nr_ficha_ocorrencia_p
			and	x.cd_procedimento   	= c.cd_procedimento)
	
union all

	select 	c.cd_procedimento,
		d.dt_instalacao,
		d.dt_retirada,
		b.cd_setor_atendimento,
		b.nr_sequencia
	from 	proc_interno a,
		atend_pac_disp_proc b,
		atend_pac_dispositivo d,
		cih_procedimento c
	where	a.nr_sequencia 		= b.nr_seq_proc_interno
	and	a.cd_proc_cih		= c.cd_procedimento
	and	d.nr_sequencia		= b.nr_seq_disp_pac
	and	d.nr_atendimento 	= nr_atendimento_w
	and	not exists (select	1
			from	cih_proc_realizado x
			where	x.nr_ficha_ocorrencia  	= nr_ficha_ocorrencia_p
			and	x.nr_seq_disp_pac_proc	= b.nr_sequencia);



BEGIN

select	nr_atendimento,
	cd_clinica
into STRICT	nr_atendimento_w,
	cd_clinica_w
from	cih_ficha_ocorrencia
where	nr_ficha_ocorrencia = nr_ficha_ocorrencia_p;

open c01;
loop
fetch c01 into
	cd_procedimento_w,
	dt_inicio_procedimento_w,
	dt_final_procedimento_w,
	cd_setor_atendimento_w,
	nr_seq_disp_pac_proc_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	select	coalesce(max(nr_seq_procedimento),0) + 1
	into STRICT	nr_seq_procedimento_w
	from	cih_proc_realizado
	where	nr_ficha_ocorrencia = nr_ficha_ocorrencia_p;

	insert into cih_proc_realizado(nr_seq_procedimento,
		nr_ficha_ocorrencia,
		cd_procedimento,
		cd_clinica,
		dt_inicio_procedimento,
		dt_final_procedimento,
		dt_atualizacao,
		nm_usuario,
		cd_setor_atendimento,
		nr_seq_disp_pac_proc)
	values (nr_seq_procedimento_w,
		nr_ficha_ocorrencia_p,
		cd_procedimento_w,
		cd_clinica_w,
		coalesce(dt_inicio_procedimento_w,clock_timestamp()),
		dt_final_procedimento_w,
		clock_timestamp(),
		nm_usuario_p,
		cd_setor_atendimento_w,
		nr_seq_disp_pac_proc_w);
end loop;
close c01;

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
