SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE cih_importar_transplantes ( nr_sequencia_p text, nr_ficha_ocorrencia_p text, nm_usuario_p text) AS $body$
DECLARE


	nr_seq_orgao_w		bigint;
	ie_retransplante_w	varchar(1);
	ie_tipo_doador_w	varchar(15);
	dt_transplante_w	timestamp;


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '' AND nr_ficha_ocorrencia_p IS NOT NULL AND nr_ficha_ocorrencia_p::text <> '') then

	select	max(nr_seq_orgao),
		max(ie_retransplante),
		max(dt_transplante)
	into STRICT	nr_seq_orgao_w,
		ie_retransplante_w,
		dt_transplante_w
	from	tx_receptor
	where	nr_sequencia = nr_sequencia_p;

	select	max(d.ie_tipo_doador)
	into STRICT	ie_tipo_doador_w
	from	tx_receptor a,
		tx_doador_potencial b,
		tx_doador_orgao c,
		tx_doador d
	where	b.nr_seq_receptor 	= a.nr_sequencia
	and	b.nr_seq_doador_orgao 	= c.nr_sequencia
	and	c.nr_seq_doador		= d.nr_sequencia
	and	a.nr_sequencia = nr_sequencia_p;

	if (coalesce(ie_tipo_doador_w::text, '') = '') then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(258782);
	end if;

	insert into cih_dados_transplante(nr_sequencia,
		nr_ficha_ocorrencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_receptor,
		nr_seq_orgao,
		dt_transplante,
		ie_retransplante,
		ie_tipo_doador)
	values (nextval('cih_dados_transplante_seq'),
		nr_ficha_ocorrencia_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_sequencia_p,
		nr_seq_orgao_w,
		dt_transplante_w,
		ie_retransplante_w,
		ie_tipo_doador_w);

end if;

commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
