CREATE OR REPLACE PROCEDURE cirurgia_participan_beforepost (cd_pessoa_fisica_p text, cd_funcao_medico_p bigint, nr_cirurgia_p bigint, nm_pessoa_fisica_p INOUT text, qt_participante_p INOUT bigint) AS $body$
DECLARE

 
nm_pessoa_fisica_w	varchar(100) 	:= '';
qt_funcao_medico_w	smallint	:= 0;
qt_participante_w	smallint	:= 0;


BEGIN 
 
 
 
if (cd_funcao_medico_p > 0) then 
	select	count(*) 
	into STRICT 	qt_funcao_medico_w 
	from	funcao_medico 
	where	cd_funcao 	= cd_funcao_medico_p;
 
	if (qt_funcao_medico_w = 0) then 
		--Função não cadastrada! 
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(263502);
	end if;
end if;
 
if (cd_pessoa_fisica_p > 0) then 
	nm_pessoa_fisica_w	:= substr(obter_nome_pf(cd_pessoa_fisica_p), 1, 100);
	 
	if (nr_cirurgia_p > 0) then 
		select	count(*) 
		into STRICT 	qt_participante_w 
		from 	cirurgia_participante 
		where	nr_cirurgia 		= nr_cirurgia_p 
		and		coalesce(dt_inativacao::text, '') = '' 
		and	cd_pessoa_fisica 	= cd_pessoa_fisica_p;
	end if;
end if;
 
nm_pessoa_fisica_p	:= nm_pessoa_fisica_w;
qt_participante_p	:= qt_participante_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
