CREATE OR REPLACE PROCEDURE cir_prescr_mat_bef_del_js (var_param_23_p text, ie_acao_delete_p text, nr_cirurgia_p bigint, cd_material_p bigint, qt_unitaria_p bigint, cd_fornecedor_p text, nr_prescricao_p bigint, nr_presc_mat_p bigint, nr_sequencia_p bigint, nr_seq_prescricao_p bigint, ie_acao_p bigint, ds_erro_p INOUT text, nm_usuario_p text) AS $body$
