CREATE OR REPLACE PROCEDURE ck_infor_ref_dia (ds_mensagem_p INOUT text) AS $body$
DECLARE


qt_checkup_com_pf	bigint;
qt_checkup_sem_pf	bigint;


BEGIN

select	count(*)
into STRICT	qt_checkup_com_pf
from	checkup
where	coalesce(dt_cancelamento::text, '') = ''
and	(cd_pessoa_fisica IS NOT NULL AND cd_pessoa_fisica::text <> '');

select	count(*)
into STRICT	qt_checkup_sem_pf
from	checkup
where	coalesce(dt_cancelamento::text, '') = ''
and	coalesce(cd_pessoa_fisica::text, '') = '';

ds_mensagem_p	:= obter_texto_dic_objeto(200504, wheb_usuario_pck.get_nr_seq_idioma,'QT_CHECKUP_COM_PF='||qt_checkup_com_pf||';QT_CHECKUP_SEM_PF='||qt_checkup_sem_pf);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
