CREATE OR REPLACE PROCEDURE classificar_desfecho (nr_atendimento_p bigint, nr_sequencia_p bigint ) AS $body$
DECLARE

 
cd_especialidade_w		  bigint;
nr_seq_classificacao_w		  bigint;


BEGIN 
	select	cd_especialidade 
	into STRICT  cd_especialidade_w 
	from	ATENDIMENTO_ALTA 
	where	nr_sequencia 	= nr_sequencia_p;
 
  if (cd_especialidade_w IS NOT NULL AND cd_especialidade_w::text <> '') then 
		select	max(nr_seq_classificacao) 
		into STRICT	nr_seq_classificacao_w 
		from	REGRA_CLASSIF_PARECER 
		where	cd_especialidade_medico = cd_especialidade_w;
		 
		if (nr_seq_classificacao_w IS NOT NULL AND nr_seq_classificacao_w::text <> '') then 
			update	atendimento_paciente 
			set	nr_seq_classificacao	= nr_seq_classificacao_w 
			where	nr_atendimento	= nr_atendimento_p;
		end if;
	end if;
end;
