SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE classificar_solic_contrato ( nr_solic_compra_p bigint, nm_usuario_p text) AS $body$
DECLARE


ds_tipo_servico_w	varchar(100);
ie_tipo_servico_w	varchar(15);

BEGIN

select	coalesce(obter_valor_dominio(4200,ie_tipo_servico),Wheb_mensagem_pck.get_Texto(298451)),
	coalesce(ie_tipo_servico,'XX')
into STRICT	ds_tipo_servico_w,
	ie_tipo_servico_w
from	solic_compra
where	nr_solic_compra = nr_solic_compra_p;

if (ie_tipo_servico_w <> 'CD') or (ie_tipo_servico_w <> 'SP') then

	update	solic_compra
	set	ie_tipo_servico = 'CD'
	where	nr_solic_compra = nr_solic_compra_p;

	CALL gerar_historico_solic_compra(
		nr_solic_compra_p,
		Wheb_mensagem_pck.get_Texto(298452),
		WHEB_MENSAGEM_PCK.get_texto(298456,
			'DS_TIPO_SERVICO_W='||DS_TIPO_SERVICO_W||
			';DS_TIPO_SERVICO2_W='||obter_valor_dominio(4200,'CD')),
		'CD',
		nm_usuario_p);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
