CREATE OR REPLACE PROCEDURE classify_service_pack (IE_CLASSIFICACAO_P text, NR_SERVICE_PACK_P bigint, NR_PACOTE_P bigint, NR_OLD_BUILD_P bigint, NM_USUARIO_P text) AS $body$
DECLARE


log_w varchar(4000);
old_classf_w varchar(30);
cd_versao_w varchar(10);


BEGIN
    begin
      select sp.ie_classificacao, sp.cd_versao
      into STRICT old_classf_w, cd_versao_w
      from man_service_pack_versao sp
      where sp.NR_SERVICE_PACK = NR_SERVICE_PACK_P
      and sp.NR_PACOTE = NR_PACOTE_P
      and sp.NR_OLD_BUILD = NR_OLD_BUILD_P;
      exception
      when no_data_found then
      begin
        old_classf_w := null;
      end;
    end;

    if (coalesce(old_classf_w::text, '') = '' or old_classf_w = '') then
    begin
      old_classf_w := Expressao_Pck.Obter_Desc_Expressao(327236);
    end;
    else
      old_classf_w := obter_valor_dominio(9905, old_classf_w);
    end if;

    log_w := Expressao_Pck.Obter_Desc_Expressao(1043525);

    log_w := REPLACE(log_w, '@#OLD_CLASSF@#', old_classf_w);
    log_w := REPLACE(log_w, '@#NEW_CLASSF@#', obter_valor_dominio(9905, IE_CLASSIFICACAO_P));

    insert into log_classificacao_sp(
      nr_sequencia,
      nm_usuario,
      dt_atualizacao,
      cd_versao,
      nr_service_pack,
      ds_alteracao
    ) values (
      nextval('log_classificacao_sp_seq'),
      nm_usuario_p,
      clock_timestamp(),
      cd_versao_w,
      nr_service_pack_p,
      log_w
    );

    update man_service_pack_versao sp
    set sp.IE_CLASSIFICACAO = IE_CLASSIFICACAO_P,
    sp.nm_usuario = nm_usuario_p
    where sp.NR_SERVICE_PACK = NR_SERVICE_PACK_P
    and sp.NR_PACOTE = NR_PACOTE_P
    and sp.NR_OLD_BUILD = NR_OLD_BUILD_P;

    commit;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
