SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE clear_physician_in_surgical ( nr_seq_lista_p bigint, cd_funcao_p bigint, ie_function_p text) AS $body$
DECLARE


qt_surgeon_w		integer;
qt_anesthesiologist_w	integer;
					

BEGIN

if (coalesce(nr_seq_lista_p, 0) > 0 and coalesce(cd_funcao_p, 0) > 0)then

	select  count(*)
	into STRICT    qt_surgeon_w
	from	funcao_medico fm
	where 	fm.cd_funcao = cd_funcao_p
	and 	fm.ie_cirurgiao = 'S';

	if (qt_surgeon_w > 0)then

		if ((ie_function_p IS NOT NULL AND ie_function_p::text <> '') and ie_function_p = 'SCHEDULE')then
			update	agenda_paciente
			set	cd_medico	 = NULL			
			where 	nr_sequencia 	= nr_seq_lista_p;
		else
			update	agenda_lista_espera
			set	cd_medico	 = NULL			
			where 	nr_sequencia 	= nr_seq_lista_p;
		end if;
		commit;
	end if;
			
	select  count(*)
	into STRICT    qt_anesthesiologist_w
	from	funcao_medico fm
	where 	fm.cd_funcao = cd_funcao_p
	and 	fm.ie_anestesista_principal = 'S';
			
	if (qt_anesthesiologist_w > 0)then

		if ((ie_function_p IS NOT NULL AND ie_function_p::text <> '') and ie_function_p = 'SCHEDULE')then
			update	agenda_paciente
			set	cd_anestesista	 = NULL			
			where 	nr_sequencia 	= nr_seq_lista_p;
		else
			update	agenda_lista_espera
			set	cd_anestesista	 = NULL			
			where 	nr_sequencia 	= nr_seq_lista_p;
		end if;
		commit;
	end if;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
