SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE cme_atualizar_ciclo ( nr_seq_ciclo_p bigint, ie_inicio_fim_p text, ie_repassar_p text, nm_usuario_p text) AS $body$
DECLARE



/* I - Inicio; F - Fim; R - Recalcular; C - Cancelar; D - Desfazer cancelamento*/

nr_seq_conjunto_w		bigint;
cd_estabelecimento_w		smallint;
ie_permite_ciclo_final_w		varchar(1);
cd_local_estoque_w		smallint;
ie_status_cancelamento_w		funcao_param_usuario.vl_parametro%type;
ie_status_desfaz_cancel_w		funcao_param_usuario.vl_parametro%type;
ie_estoque_valido_w			smallint;

c01 CURSOR FOR
SELECT	nr_sequencia
from	cm_conjunto_cont
where	nr_seq_ciclo	= nr_seq_ciclo_p
and	coalesce(ie_situacao,'A') = 'A';


BEGIN

select	coalesce(max(cd_estabelecimento),1)
into STRICT	cd_estabelecimento_w
from	cm_conjunto_cont
where	nr_seq_ciclo = nr_seq_ciclo_p
and	coalesce(ie_situacao,'A') = 'A';


select	Obter_Valor_Param_Usuario(406,5,Obter_Perfil_ativo,nm_usuario_p,cd_estabelecimento_w)
into STRICT	ie_permite_ciclo_final_w
;

select	max(coalesce(Obter_Valor_Param_Usuario(406,78,Obter_Perfil_ativo,nm_usuario_p,cd_estabelecimento_w),0))
into STRICT	cd_local_estoque_w
;

select	max(coalesce(Obter_Valor_Param_Usuario(406,108,Obter_Perfil_ativo,nm_usuario_p,cd_estabelecimento_w),1))
into STRICT	ie_status_cancelamento_w
;

select	max(coalesce(Obter_Valor_Param_Usuario(406,109,Obter_Perfil_ativo,nm_usuario_p,cd_estabelecimento_w),1))
into STRICT	ie_status_desfaz_cancel_w
;

if (ie_inicio_fim_p	= 'I') then
	begin

	update	cm_ciclo
	set	dt_inicio	= clock_timestamp(),
		dt_cancelamento  = NULL,
		nr_seq_motivo_cancel  = NULL,
		nm_usuario_cancel  = NULL
	where	nr_sequencia	= nr_seq_ciclo_p
	and	coalesce(dt_inicio::text, '') = '';

	update	cm_conjunto_cont
	set	ie_status_conjunto	= '5',
		nm_usuario_inicio = nm_usuario_p,
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where	nr_seq_ciclo	= nr_seq_ciclo_p
	and	ie_status_conjunto <> 5;

	end;
elsif (ie_inicio_fim_p	= 'F') then
	begin

	update	cm_conjunto_cont
	set	ie_status_conjunto	= '3',
		nm_usuario_fim = nm_usuario_p,
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where	nr_seq_ciclo	= nr_seq_ciclo_p
	and	ie_status_conjunto <> 3;

	if (ie_repassar_p = 'S') then
		update	cm_conjunto_cont
		set	dt_saida		= clock_timestamp(),
			nm_usuario_saida 	= nm_usuario_p,
			cd_local_estoque	= cd_local_estoque,
			cd_setor_atend_dest	= coalesce(cd_setor_atend_dest,cd_setor_atend_orig),
			cd_pessoa_fisica	= cd_pessoa_fisica,
			cd_cgc			= cd_cgc,
			nm_usuario = nm_usuario_p,
			dt_atualizacao = clock_timestamp()
		where	nr_seq_ciclo		= nr_seq_ciclo_p
		and	ie_status_conjunto	= 3;
	end if;
	
	

	if (cd_local_estoque_w <> 0) then
		begin
		
		select count(*)
		into STRICT ie_estoque_valido_w
		from local_estoque
		where cd_local_estoque = cd_local_estoque_w;
		
		if (ie_estoque_valido_w = 1) then
			update	cm_conjunto_cont
			set	cd_local_estoque = cd_local_estoque_w,
				nm_usuario = nm_usuario_p,
				dt_atualizacao = clock_timestamp()
			where	nr_seq_ciclo = nr_seq_ciclo_p
			and	ie_status_conjunto = 3;
		end if;
		end;
	end if;

	update	cm_ciclo
	set	dt_fim		= clock_timestamp()
	where	nr_sequencia	= nr_seq_ciclo_p
	and	(dt_inicio IS NOT NULL AND dt_inicio::text <> '')
	and	coalesce(dt_fim::text, '') = '';

	end;

elsif (ie_inicio_fim_p	= 'C') then
	begin
	
	update	cm_ciclo
	set	dt_inicio	 = NULL,
		dt_conferencia  = NULL,
		nm_usuario_conferencia  = NULL,
		dt_cancelamento = clock_timestamp(),
		nm_usuario_cancel = nm_usuario_p
	where	nr_sequencia	= nr_seq_ciclo_p
	and	(dt_inicio IS NOT NULL AND dt_inicio::text <> '');
	
	update	cm_conjunto_cont
	set	ie_status_conjunto	= ie_status_cancelamento_w
	where	nr_seq_ciclo	= nr_seq_ciclo_p;
	
	end;

elsif (ie_inicio_fim_p	= 'D') then
	begin

	update	cm_ciclo
	set	dt_cancelamento  = NULL,
		nr_seq_motivo_cancel  = NULL,
		nm_usuario_cancel  = NULL
	where	nr_sequencia	= nr_seq_ciclo_p
	and	(dt_cancelamento IS NOT NULL AND dt_cancelamento::text <> '');

	update	cm_conjunto_cont
	set	ie_status_conjunto	= ie_status_desfaz_cancel_w,
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where	nr_seq_ciclo	= nr_seq_ciclo_p;

	end;
	
elsif (ie_inicio_fim_p	= 'R') and /*Matheus OS 46153 em 15/12/2006*/
	(ie_permite_ciclo_final_w = 'S') then
	begin
	update	cm_conjunto_cont
	set	ie_status_conjunto	= '3',
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where	nr_seq_ciclo	= nr_seq_ciclo_p
	and	ie_status_conjunto <> 3;

	if (ie_repassar_p = 'S') then
		update	cm_conjunto_cont
		set	dt_saida		= clock_timestamp(),
			nm_usuario_saida 	= nm_usuario_p,
			cd_local_estoque	= cd_local_estoque,
			cd_setor_atend_dest	= coalesce(cd_setor_atend_dest,cd_setor_atend_orig),
			cd_pessoa_fisica	= cd_pessoa_fisica,
			cd_cgc			= cd_cgc,
			nm_usuario 	= nm_usuario_p,
			dt_atualizacao 	= clock_timestamp()
		where	nr_seq_ciclo		= nr_seq_ciclo_p
		and	ie_status_conjunto	= 3;
	end if;
	end;
end if;

open	C01;
loop
fetch c01 into
	nr_seq_conjunto_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	CALL CME_Calcular_Preco_Ester(nr_seq_conjunto_w, nm_usuario_p,ie_inicio_fim_p);
	exception
	when no_data_found then
		-- Regra de calculo de valor nao encontrada!' || chr(13) || 'Verifique nos parametros CME (Cadastros).
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(264812);
	end;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE cme_atualizar_ciclo ( nr_seq_ciclo_p bigint, ie_inicio_fim_p text, ie_repassar_p text, nm_usuario_p text) FROM PUBLIC;

