SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE cme_atualiza_validade_lote ( nr_seq_conj_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_ciclo_w		bigint;
nr_seq_embalagem_w	bigint;
nr_seq_metodo_w		bigint;
dt_fim_ciclo_w		timestamp;
qt_dia_validade_w	integer;
qt_hora_validade_w	integer;
ie_indeterminado_w	varchar(1);


BEGIN

select	max(nr_seq_ciclo),
	max(nr_seq_embalagem)
into STRICT	nr_seq_ciclo_w,
	nr_seq_embalagem_w
from	cm_conjunto_cont
where	nr_sequencia = nr_seq_conj_p
and	coalesce(ie_situacao,'A') = 'A';

select	b.nr_seq_classif,
	a.dt_fim
into STRICT	nr_seq_metodo_w,
	dt_fim_ciclo_w
from	cm_equipamento b,
	cm_ciclo a
where	a.nr_sequencia = nr_seq_ciclo_w
and	a.nr_seq_equipamento = b.nr_sequencia;

select	coalesce(max(coalesce(a.qt_dia_validade,0)),0),
	coalesce(max(coalesce(a.qt_hor_validade,0)),0),
	coalesce(max(ie_indeterminado),'N')
into STRICT	qt_dia_validade_w,
	qt_hora_validade_w,
	ie_indeterminado_w
from	cm_validade a
where	a.nr_seq_embalagem = nr_seq_embalagem_w
and	a.nr_seq_metodo = nr_seq_metodo_w;

if (qt_dia_validade_w > 0) then

	update	cm_conjunto_cont
	set	dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p,
		dt_validade = trunc(coalesce(dt_fim_ciclo_w,clock_timestamp()) + qt_dia_validade_w,'dd')
	where	nr_sequencia = nr_seq_conj_p;

elsif (qt_hora_validade_w > 0) then

	update	cm_conjunto_cont
	set	dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p,
		dt_validade = to_date(to_char(coalesce(dt_fim_ciclo_w,clock_timestamp()) + qt_hora_validade_w / 24,'dd/mm/yyyy hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss')
	where	nr_sequencia = nr_seq_conj_p;

elsif (ie_indeterminado_w = 'S') then

	update	cm_conjunto_cont
	set	dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p,
		dt_validade  = NULL,
		ie_indeterminado = 'S'
	where	nr_sequencia = nr_seq_conj_p;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
