SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE cme_baixar_conj_descartavel () AS $body$
DECLARE


nr_seq_req_w		bigint;
nr_req_conj_w		bigint;
nm_usuario_w		varchar(12);

c01 CURSOR FOR
	SELECT	nr_sequencia
	from	cm_requisicao
	where	dt_baixa between(clock_timestamp() - interval '6 days') and (clock_timestamp() - interval '1 days');

c02 CURSOR FOR
	SELECT	a.nr_sequencia
	from	cm_requisicao_conj a,
		cm_requisicao_item b
	where	a.nr_seq_item_req = b.nr_sequencia
	and	b.nr_seq_requisicao = nr_seq_req_w
	and	cm_obter_se_apenas_descartavel(b.nr_seq_conjunto) = 'S'
	and	coalesce(a.dt_retorno::text, '') = '';


BEGIN
nm_usuario_w := wheb_usuario_pck.get_nm_usuario;

open c01;
loop
fetch c01 into
	nr_seq_req_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	open c02;
	loop
	fetch c02 into
		nr_req_conj_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */
		begin

		update	cm_requisicao_conj
		set	dt_retorno = clock_timestamp(),
			nm_usuario_retorno = nm_usuario_w
		where	nr_sequencia = nr_req_conj_w;

		end;
	end loop;
	close c02;

	end;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
