DECLARE


nr_seq_conjunto_w	cm_conjunto.nr_sequencia%type;
qt_registro_w		integer;


BEGIN

begin
select	nr_seq_conjunto
into STRICT	nr_seq_conjunto_w
from	cm_cod_barras
where	cd_barra = cd_barras_p;
exception
when others then
	nr_seq_conjunto_w := 0;
end;

