CREATE OR REPLACE PROCEDURE cme_excluir_vinculo_conjunto ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


nm_conjunto_w        cm_conjunto.nm_conjunto%type;
existe_conjunto_w    integer;
ds_mensagem_w        varchar(255);


BEGIN

    delete from    cm_conjunto_cota where nr_seq_conjunto = nr_sequencia_p;
    delete from    cm_conjunto_item where nr_seq_conjunto = nr_sequencia_p;
    delete from    cm_cod_barras where nr_seq_conjunto = nr_sequencia_p;
    delete from    cm_cores_conjunto where nr_seq_conjunto = nr_sequencia_p;
    delete from    cm_equip_conjunto where nr_seq_conjunto = nr_sequencia_p;
    delete from    cm_manutencao_conj where nr_seq_conjunto = nr_sequencia_p;
    delete from    conjunto_historico where nr_conjunto = nr_sequencia_p;
    delete from    cm_imagem_conjunto where nr_seq_conjunto = nr_sequencia_p;
    delete from    proc_interno_cme where nr_seq_conjunto = nr_sequencia_p;

    begin
    select 1
    into STRICT  existe_conjunto_w
    from  cm_conjunto
    where nr_sequencia = nr_sequencia_p  LIMIT 1;
    exception
    when no_data_found then
    existe_conjunto_w := 0;
    end;

    if (existe_conjunto_w = 1) then
            select    nm_conjunto
            into STRICT    nm_conjunto_w
            from    cm_conjunto
            where    nr_sequencia = nr_sequencia_p;
            delete from cm_conjunto where nr_sequencia = nr_sequencia_p;

        ds_mensagem_w := WHEB_MENSAGEM_PCK.get_texto(nr_seq_mensagem_p => 1187372,
                                                    Vl_Macros_P        => 'NR_SEQ_CONJUNTO= ' || nr_sequencia_p || ';NM_CONJUNTO= '|| nm_conjunto_W);

         CALL gravar_log_exclusao(nm_tabela_p     => 'CM_CONJUNTO',
                            nm_usuario_p     => nm_usuario_p,
                            ds_chave_p       => ds_mensagem_w,
                            ie_executar_commit_p => 'S');
    end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
