CREATE OR REPLACE PROCEDURE cme_gerar_requisicao_dia ( cd_agenda_p bigint, dt_agenda_p timestamp, cd_pessoa_fisica_p text, cd_setor_atendimento_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_agenda_w		agenda_paciente.nr_sequencia%type;
nr_seq_requisicao_w	bigint;

c01 CURSOR FOR
	SELECT	nr_sequencia
	from	agenda_paciente
	where	trunc(dt_agenda) = trunc(dt_agenda_p)
	and	((cd_agenda = cd_agenda_p)  or (cd_agenda_p = 0))
	and      ie_status_agenda not in ('L','I','C','B','F')
	and	obter_estab_agenda(cd_agenda) = cd_estabelecimento_p;


BEGIN
open c01;
loop
fetch c01 into
	nr_seq_agenda_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin	
	nr_seq_requisicao_w := cme_gerar_requisicao(nr_seq_agenda_w, cd_pessoa_fisica_p, cd_setor_atendimento_p, cd_estabelecimento_p, nm_usuario_p, nr_seq_requisicao_w);
	end;
end loop;
close c01;

commit;

end;
$body$
