CREATE OR REPLACE PROCEDURE cme_gerar_retorno_conj_terc ( nr_seq_receb_externo_p bigint, nr_seq_conjunto_p bigint, qt_conjunto_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_receb_externo_w		bigint;
qt_conj_receb_w			bigint;
qt_devolvida_w			bigint;


BEGIN

select	coalesce(min(nr_sequencia),0)
into STRICT	nr_seq_receb_externo_w
from	cm_receb_externo_conj
where	nr_seq_receb_externo = nr_seq_receb_externo_p
and	nr_seq_conjunto = nr_seq_conjunto_p;

if (nr_seq_receb_externo_w > 0) then
	begin

	update	cm_receb_externo_conj
	set	qt_devolvida = (coalesce(qt_devolvida,0) + coalesce(qt_conjunto_p,0))
	where	nr_seq_receb_externo = nr_seq_receb_externo_p
	and	nr_sequencia = nr_seq_receb_externo_w;

	end;
end if;

select	coalesce(sum(qt_conj_receb),0),
	coalesce(sum(qt_devolvida),0)
into STRICT	qt_conj_receb_w,
	qt_devolvida_w
from	cm_receb_externo a,
	cm_receb_externo_conj b
where	a.nr_sequencia = b.nr_seq_receb_externo
and	b.nr_seq_receb_externo = nr_seq_receb_externo_p
and	coalesce(a.dt_devolucao::text, '') = '';

if (qt_conj_receb_w = qt_devolvida_w) then

	update	cm_receb_externo
	set	dt_devolucao = clock_timestamp()
	where	nr_sequencia = nr_seq_receb_externo_p;

end if;

commit;

end;
$body$
