SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE cme_incluir_itens_controle (nr_seq_conj_cont_p bigint, nm_usuario_p text, ie_abort_items_p text default null) AS $body$
DECLARE


nr_seq_conjunto_w		bigint;
nr_seq_item_w			bigint;
qt_item_w			double precision;
nr_seq_cont_w			bigint;
nr_seq_conjunto_sub_w		bigint;
qt_existe_substituto_w		bigint;
ie_situacao_w			varchar(1);
ie_item_found_w         varchar(1);
ie_status_w			varchar(15);

c01 CURSOR FOR
	SELECT	a.nr_seq_item,
		a.qt_item,
		coalesce(a.ie_status_item,'1')
	from	cm_conjunto_item a
	where	a.nr_seq_conjunto	= nr_seq_conjunto_w;


BEGIN

select	nr_seq_conjunto
into STRICT	nr_seq_conjunto_w
from	cm_conjunto_cont
where	nr_sequencia		= nr_seq_conj_cont_p
and	coalesce(ie_situacao,'A') = 'A';

delete	from cm_item_cont
where	nr_seq_conjunto		= nr_seq_conj_cont_p;

open 	c01;
loop
fetch	c01 into
	nr_seq_item_w,
	qt_item_w,
	ie_status_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	max(ie_situacao)
	into STRICT	ie_situacao_w
	from	cm_item
	where	nr_sequencia = nr_seq_item_w;

	if (ie_situacao_w = 'A') then
		begin
		
		ie_item_found_w := 'S';

		select	count(*)
		into STRICT	qt_existe_substituto_w
		from	cm_conjunto_item_sub
		where	nr_seq_conjunto = nr_seq_conjunto_w
		and	nr_seq_item = nr_seq_item_w
		and	coalesce(dt_final_vigencia::text, '') = '';

		if (qt_existe_substituto_w > 0) then
			begin
		
			select	max(nr_seq_item_sub),
				max(nr_seq_conjunto_sub)
			into STRICT	nr_seq_item_w,
				nr_seq_conjunto_sub_w
			from	cm_conjunto_item_sub
			where	nr_seq_conjunto = nr_seq_conjunto_w
			and	nr_seq_item = nr_seq_item_w
			and	coalesce(dt_final_vigencia::text, '') = '';
	
			select	max(qt_item)
			into STRICT	qt_item_w
			from	cm_conjunto_item
			where	nr_seq_conjunto = nr_seq_conjunto_sub_w
			and	nr_seq_item = nr_seq_item_w;

			end;
		end if;
	
		select	nextval('cm_item_cont_seq')
		into STRICT	nr_seq_cont_w
		;

		insert	into cm_item_cont(nr_sequencia,
			nr_seq_conjunto,
			nr_seq_item,
			qt_item,
			dt_atualizacao,
			nm_usuario,
			ie_status)
		values (nr_seq_cont_w,
			nr_seq_conj_cont_p,
			nr_seq_item_w,
			qt_item_w,
			clock_timestamp(),
			nm_usuario_p,
			ie_status_w);
		end;
	end if;

	end;
end loop;
close c01;

if (coalesce(ie_item_found_w::text, '') = '' and (ie_abort_items_p IS NOT NULL AND ie_abort_items_p::text <> '')) then
    CALL wheb_mensagem_pck.exibir_mensagem_abort(wheb_mensagem_pck.get_texto(1165956));
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
