		dt_atualizacao,
		nm_usuario,
		cd_estabelecimento)
	values (nextval('cm_grupo_classif_seq'),
		nr_seq_grupo_w,
		nr_seq_classif_p,
		clock_timestamp(),
		nm_usuario_p,
		wheb_usuario_pck.get_cd_estabelecimento);
	end;
end loop;
close C01;

commit;

end;
