CREATE OR REPLACE PROCEDURE cme_inserir_item_conjunto ( nr_seq_item_p bigint, qt_item_p bigint, ds_conjuntos_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_conjunto_w		bigint;
ie_status_itens_conjunto_w	varchar(15);
ie_incr_seq_interna_w		varchar(1);
nr_seq_interno_w		bigint;
ie_forma_codigo_w		varchar(15);
cd_codigo_item_w		varchar(255);

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	cm_conjunto
	where	obter_Se_contido(nr_sequencia,ds_conjuntos_p) = 'S';


BEGIN

nr_seq_interno_w := 0;

ie_status_itens_conjunto_w := Obter_Param_Usuario(406, 211, Obter_Perfil_Ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, ie_status_itens_conjunto_w);
ie_incr_seq_interna_w := Obter_Param_Usuario(406, 212, Obter_Perfil_Ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, ie_incr_seq_interna_w);
ie_forma_codigo_w := Obter_Param_Usuario(406, 61, Obter_Perfil_Ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, ie_forma_codigo_w);

/* Inserir o item nos conjuntos selecionados */

open C01;
loop
fetch C01 into
	nr_seq_conjunto_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	if (ie_incr_seq_interna_w = 'S') then
		select	coalesce(max(nr_seq_interno),0) + 1
		into STRICT	nr_seq_interno_w
		from	cm_conjunto_item
		where	nr_seq_conjunto = nr_seq_conjunto_w;
	end if;

	cd_codigo_item_w := cm_gerar_codigo_item_conj(nr_seq_conjunto_w, wheb_usuario_pck.get_cd_estabelecimento, nr_seq_interno_w, ie_forma_codigo_w, nm_usuario_p, cd_codigo_item_w);

	insert into cm_conjunto_item(
		nr_seq_conjunto,
		nr_seq_interno,
		nr_seq_item,
		qt_item,
		dt_atualizacao,
		nm_usuario,
		ie_indispensavel,
		ie_status_item,
		cd_codigo)
	values (
		nr_seq_conjunto_w,
		CASE WHEN nr_seq_interno_w=0 THEN null  ELSE nr_seq_interno_w END ,
		nr_seq_item_p,
		qt_item_p,
		clock_timestamp(),
		nm_usuario_p,
		'N',
		ie_status_itens_conjunto_w,
		cd_codigo_item_w);

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
