SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE cm_adiciona_conj_req_barras ( nr_requisicao_p bigint, nr_seq_conjunto_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_conjunto_w		bigint;
qt_conjunto_w		bigint;
nr_sequencia_w		bigint;



BEGIN

nr_seq_conjunto_w	:= coalesce(nr_seq_conjunto_p,0);

select	count(*)
into STRICT	qt_conjunto_w
from	cm_conjunto
where	nr_sequencia = nr_seq_conjunto_w
and	ie_situacao = 'A';

if (qt_conjunto_w = 0) then
	-- Erro ao ler o código de barras. Conjunto não existente ou inativo!
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(264811);
end if;

select	nextval('cm_requisicao_item_seq')
into STRICT	nr_sequencia_w
;

insert into cm_requisicao_item(
	nr_sequencia,
	nr_seq_requisicao,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	nr_seq_conjunto,
	qt_conjunto,
	ie_devolucao,
	ie_emprestimo) values (
		nr_sequencia_w,
		nr_requisicao_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_conjunto_w,
		1,
		'N',
		'N');

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
