SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE cm_atualiza_ciclo_movto (nm_usuario_p text, ds_observacao_p text, ie_tipo_movto_p text, nr_seq_ciclo_p bigint, nr_seq_ciclo_lav_p bigint) AS $body$
DECLARE

	
nr_sequencia_w		bigint;
ds_observacao_w		varchar(255);
cd_estabelecimento_w	integer;
ie_grava_hist_obs_w	varchar(1);
	

BEGIN
if (nr_seq_ciclo_p > 0) then
	select	cd_estabelecimento
	into STRICT	cd_estabelecimento_w
	from	cm_ciclo
	where	nr_sequencia = nr_seq_ciclo_p;
else
	select	cd_estabelecimento
	into STRICT	cd_estabelecimento_w
	from	cm_ciclo_lavacao
	where	nr_sequencia = nr_seq_ciclo_lav_p;
end if;

ie_grava_hist_obs_w := obter_valor_param_usuario(406, 136, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w);

select	nextval('cm_ciclo_movto_seq')
into STRICT	nr_sequencia_w
;

insert into	cm_ciclo_movto(nr_sequencia,
		dt_atualizacao,         
 	   	nm_usuario,             
 		dt_atualizacao_nrec,    
 		nm_usuario_nrec,        
 		nr_seq_ciclo,
		nr_seq_ciclo_lav,
 		dt_movimento,           
 		ie_tipo_movto,          
 		ds_observacao) values (nr_sequencia_w,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				CASE WHEN nr_seq_ciclo_p=0 THEN null  ELSE nr_seq_ciclo_p END ,
				CASE WHEN nr_seq_ciclo_lav_p=0 THEN null  ELSE nr_seq_ciclo_lav_p END ,
				clock_timestamp(),
				ie_tipo_movto_p,
				ds_observacao_p);

if (ie_tipo_movto_p = 'C') and (ie_grava_hist_obs_w = 'S') and (ds_observacao_p IS NOT NULL AND ds_observacao_p::text <> '') then
	begin

	if (nr_seq_ciclo_p > 0) then
		select	substr(CASE WHEN coalesce(ds_observacao::text, '') = '' THEN ds_observacao_p  ELSE ds_observacao || chr(13) || chr(10) || ds_observacao_p END ,1,255)
		into STRICT	ds_observacao_w
		from	cm_ciclo
		where	nr_sequencia = nr_seq_ciclo_p;

		update	cm_ciclo
		set	ds_observacao = ds_observacao_w
		where	nr_sequencia = nr_seq_ciclo_p;
	else
		select	substr(CASE WHEN coalesce(ds_observacao::text, '') = '' THEN ds_observacao_p  ELSE ds_observacao || chr(13) || chr(10) || ds_observacao_p END ,1,255)
		into STRICT	ds_observacao_w
		from	cm_ciclo_lavacao
		where	nr_sequencia = nr_seq_ciclo_lav_p;

		update	cm_ciclo_lavacao
		set	ds_observacao = ds_observacao_w
		where	nr_sequencia = nr_seq_ciclo_lav_p;
	end if;
	

	end;
end if;

if (nr_seq_ciclo_p >0) then

	update 	cm_conjunto
	set 	ie_fast_tag = 'N'
	where 	nr_sequencia in (SELECT nr_seq_conjunto
				from cm_conjunto_cont
				where nr_seq_ciclo = nr_seq_ciclo_p);

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
