CREATE OR REPLACE PROCEDURE cm_atualiza_conferencia_ciclo ( nr_seq_ciclo_p bigint, nr_seq_ciclo_lav_p bigint, nm_usuario_p text) AS $body$
BEGIN

if (nr_seq_ciclo_p > 0) then

	update	cm_ciclo
	set	dt_conferencia = clock_timestamp(),
		nm_usuario_conferencia = nm_usuario_p
	where	nr_sequencia = nr_seq_ciclo_p;

else

	update	cm_ciclo_lavacao
	set	dt_conferencia = clock_timestamp(),
		nm_usuario_conferencia = nm_usuario_p
	where	nr_sequencia = nr_seq_ciclo_lav_p;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
