CREATE OR REPLACE PROCEDURE cm_baixar_conj_req_pendente ( cd_estabelecimento_p bigint default null) AS $body$
DECLARE


cd_motivo_baixa_req_w		cm_parametro.cd_motivo_baixa_req%type;
cd_estabelecimento_w		estabelecimento.cd_estabelecimento%type;
nr_requisicao_w			cm_requisicao.nr_sequencia%type;

c01 CURSOR FOR
	SELECT	nr_sequencia
	from	cm_requisicao
	where	coalesce(dt_baixa::text, '') = ''
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
	and	dt_liberacao < (clock_timestamp() - interval '1 days');


BEGIN

begin
cd_estabelecimento_w := coalesce(cd_estabelecimento_p, obter_estabelecimento_ativo);
exception
when others then
	cd_estabelecimento_w := 1;
end;

select	coalesce(max(cd_motivo_baixa_req),0)
into STRICT	cd_motivo_baixa_req_w
from	cm_parametro
where	cd_estabelecimento = cd_estabelecimento_w;

open c01;
loop
fetch c01 into
	nr_requisicao_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	CALL cm_baixa_motivo_req(	nr_requisicao_w,
				cd_motivo_baixa_req_w,
				'JOB_BCRP');
	end;
end loop;
close c01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
