CREATE OR REPLACE PROCEDURE cm_cod_envio_ext_manual ( nr_seq_item_p bigint, nr_seq_envio_p bigint, nm_usuario_p text) AS $body$
DECLARE

		
cd_codigo_w 	cm_item.cd_codigo%type;
		

BEGIN

select 	max(cd_codigo)
into STRICT	cd_codigo_w
from 	cm_item 
where 	nr_sequencia = nr_seq_item_p;

if (coalesce(cd_codigo_w,'0') <> '0')  then

	update	cm_envio_externo_item
	set	cd_codigo = cd_codigo_w
	where	nr_seq_item = nr_seq_item_p
	and	nr_seq_envio = nr_seq_envio_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
