CREATE OR REPLACE PROCEDURE cm_confirmar_conj_cirurgia (nr_cirurgia_p bigint, nr_seq_pepo_p bigint, cd_barras_p bigint, nm_usuario_p text, ie_opcao_p text) AS $body$
DECLARE

qt_conjunto_w smallint;
dt_liberacao_w timestamp;


BEGIN
if (coalesce(cd_barras_p,0) > 0) then
	if (coalesce(nr_cirurgia_p,0) > 0) then
		select	count(nr_sequencia),
			max(dt_liberacao)
		into STRICT	qt_conjunto_w,
			dt_liberacao_w
		from	cm_conjunto_cont
		where	nr_cirurgia = nr_cirurgia_p
		and 	nr_sequencia = cd_barras_p;


	end if;

	if (coalesce(qt_conjunto_w,0) = 0) then
		select	count(nr_sequencia),
			max(dt_liberacao)
		into STRICT	qt_conjunto_w,
			dt_liberacao_w
		from	cm_conjunto_cont
		where	nr_seq_pepo = nr_seq_pepo_p
		and 	nr_sequencia = cd_barras_p;


	end if;

	if (coalesce(qt_conjunto_w,0) > 0 ) then
		if (coalesce(dt_liberacao_w::text, '') = '') then
			if (ie_opcao_p = 'D') then
				update 	cm_conjunto_cont
				set      	dt_baixa_cirurgia  = NULL,
					nm_usuario_baixa_cirurgia  = NULL
				where   	nr_sequencia = cd_barras_p
				and	coalesce(dt_liberacao::text, '') = '';
			else
				update 	 cm_conjunto_cont
				set   	 dt_baixa_cirurgia = clock_timestamp(),
					nm_usuario_baixa_cirurgia = nm_usuario_p
				where   	nr_sequencia = cd_barras_p
				and	coalesce(dt_liberacao::text, '') = '';
			end if;
		else
			--'Conjunto vinculado a cirurgia está liberado!
			CALL wheb_mensagem_pck.exibir_mensagem_abort(405961);
		end if;
	else
		--'Conjunto não encontrado ou não está vinculado a cirurgia!
		CALL wheb_mensagem_pck.exibir_mensagem_abort(200976);

	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
