SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE cm_consiste_gerar_transf ( nr_seq_lote_transf_p bigint, ie_conjunto_item_p text, cd_estabelecimento_p bigint, nm_usuario_p text, ds_consistencia_p INOUT text) AS $body$
DECLARE


nr_seq_conjunto_item_w		bigint;
nm_conjunto_item_w		varchar(255);
ds_consistencia_w			varchar(2000);
ds_consistencia_ww			varchar(2000);

c01 CURSOR FOR
SELECT	c.nr_sequencia
from	cm_conjunto_cont a,
	cm_lote_transf_conj b,
	cm_conjunto c
where	a.nr_sequencia = b.nr_seq_conjunto
and	c.nr_sequencia = a.nr_seq_conjunto
and	b.nr_seq_lote_transf = nr_seq_lote_transf_p
and	coalesce(c.cd_material::text, '') = ''
and	ie_conjunto_item_p = 'C'
group by	c.nr_sequencia

union

SELECT	c.nr_sequencia
from	cm_item_cont a,
	cm_lote_transf_conj b,
	cm_item c
where	a.nr_seq_conjunto = b.nr_seq_conjunto
and	c.nr_sequencia = a.nr_seq_item
and	b.nr_seq_lote_transf = nr_seq_lote_transf_p
and	coalesce(c.cd_material::text, '') = ''
and	ie_conjunto_item_p = 'I'
group by	c.nr_sequencia;


BEGIN

/* Consistir se conjunto/item possui material vinculado */

open c01;
loop
fetch c01 into
	nr_seq_conjunto_item_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	if (ie_conjunto_item_p = 'C') then
		begin
		
		select	nm_conjunto
		into STRICT	nm_conjunto_item_w
		from	cm_conjunto
		where	nr_sequencia = nr_seq_conjunto_item_w;

		ds_consistencia_ww := wheb_mensagem_pck.get_texto(802926,'nm_conjunto_item_w=' || nm_conjunto_item_w || ';' || 'nr_seq_conjunto_item_w=' || nr_seq_conjunto_item_w) || chr(10) || chr(13);

		if (length(ds_consistencia_w || ds_consistencia_ww) <= 255) then
			ds_consistencia_w := ds_consistencia_w || ds_consistencia_ww;
		else
		  exit;
		end if;							

		end;
	elsif (ie_conjunto_item_p = 'I') then
		begin

		select	nm_item
		into STRICT	nm_conjunto_item_w
		from	cm_item
		where	nr_sequencia = nr_seq_conjunto_item_w;
	
		ds_consistencia_ww := wheb_mensagem_pck.get_texto(802927,'nm_conjunto_item_w=' || nm_conjunto_item_w || ';' || 'nr_seq_conjunto_item_w=' || nr_seq_conjunto_item_w) || chr(10) || chr(13);

		if (length(ds_consistencia_w || ds_consistencia_ww) <= 255) then
			ds_consistencia_w := ds_consistencia_w || ds_consistencia_ww;
		else
		  exit;
		end if;
		
		end;
	end if;

	end;
end loop;
close c01;

ds_consistencia_p	:= ds_consistencia_w;	

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
