SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE cm_converte_cod_barras ( cd_barras_p text, cd_codigo_p INOUT text, ds_erro_p INOUT text, ie_tipo_est_p text default 'C') AS $body$
DECLARE


ds_erro_w		varchar(2000) := '';
ds_retorno_w		varchar(2000) := '';
ie_barras_w		varchar(10) := '';
qt_existe_w		integer;
cd_barras_w		varchar(50);
nr_sequencia_w	cm_item.nr_sequencia%type;


BEGIN

if (coalesce(ie_tipo_est_p, 'C') = 'C') then

	if (substr(cd_barras_p,1,1) = 'A') then

		select	count(*)
		into STRICT	qt_existe_w
		from	cm_cod_barras
		where	cd_barra = cd_barras_p;

		if (qt_existe_w > 0) then
			select	nr_seq_conjunto
			into STRICT	cd_codigo_p
			from	cm_cod_barras
			where	cd_barra = cd_barras_p;
		end if;

	else

		begin
		select	count(*)
		into STRICT	qt_existe_w
		from	cm_conjunto
		where	nr_sequencia = cd_barras_p
		and	ie_situacao = 'A';
		exception
			when others then
			qt_existe_w := 0;
		end;

		if (qt_existe_w > 0) then
			cd_codigo_p := cd_barras_p;
		end if;

	end if;

elsif (coalesce(ie_tipo_est_p, 'C') = 'I') then

	select 	max(nr_sequencia)
	into STRICT	nr_sequencia_w
	from    cm_item
	where  	cd_codigo = cd_barras_p
	and    	ie_unico = 'S';

	if (coalesce(nr_sequencia_w::text, '') = '') then

		begin
		select 	count(*)
		into STRICT	qt_existe_w
		from   	cm_item
		where  	nr_sequencia = cd_barras_p
		and    	ie_unico = 'S';
		exception
			when others then
			qt_existe_w := 0;
		end;

		if (qt_existe_w > 0) then
			cd_codigo_p := cd_barras_p;
		end if;
	else
		cd_codigo_p := nr_sequencia_w;
	end if;


end if;

if (qt_existe_w = 0) then
	ds_erro_p := WHEB_MENSAGEM_PCK.get_texto(279097);
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
