CREATE OR REPLACE PROCEDURE cm_desfaz_inicio_fim_ciclo ( nr_seq_ciclo_p bigint, ie_opcao_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_existe_w		bigint;
ie_fim_ciclo_w		varchar(1);
ie_inicio_ciclo_w	varchar(1);


BEGIN
ie_fim_ciclo_w := obter_param_usuario(406, 182, Obter_perfil_Ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, ie_fim_ciclo_w);
ie_inicio_ciclo_w := obter_param_usuario(406, 183, Obter_perfil_Ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, ie_inicio_ciclo_w);

if (ie_opcao_p = 1) then
	begin

	update	cm_ciclo
	set	dt_inicio  = NULL,
		dt_conferencia  = NULL,
		nm_usuario_conferencia  = NULL
	where	nr_sequencia = nr_seq_ciclo_p;

	CALL cm_gerar_hist_ciclo(obter_desc_expressao(726140),'D',nr_seq_ciclo_p,null,nm_usuario_p);

	if (ie_inicio_ciclo_w = 'S') then
		select	count(*)
		into STRICT	qt_existe_w
		from	cm_conjunto_cont
		where	nr_seq_ciclo = nr_seq_ciclo_p;

		if (qt_existe_w > 0) then
			update	cm_conjunto_cont
			set	ie_status_conjunto = 1
			where	nr_seq_ciclo = nr_seq_ciclo_p;
		end if;
	end if;

	end;
elsif (ie_opcao_p = 2) then
	begin

	update	cm_ciclo
	set	dt_fim  = NULL
	where	nr_sequencia = nr_seq_ciclo_p;

	CALL cm_gerar_hist_ciclo(wheb_mensagem_pck.get_texto(311251),'D',nr_seq_ciclo_p,null,nm_usuario_p);

	if (ie_fim_ciclo_w = 'S') then
		select	count(*)
		into STRICT	qt_existe_w
		from	cm_conjunto_cont
		where	nr_seq_ciclo = nr_seq_ciclo_p;

		if (qt_existe_w > 0) then
			update	cm_conjunto_cont
			set	ie_status_conjunto = 5
			where	nr_seq_ciclo = nr_seq_ciclo_p;
		end if;
	end if;

	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
