SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE cm_estorna_liberacao_transf ( nr_seq_lote_transf_p bigint, ie_transf_receb_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_nota_fiscal_w	bigint;


BEGIN

if (ie_transf_receb_p = 'T') then
	begin

	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_seq_nota_fiscal_w
	from	nota_fiscal
	where	nr_seq_transf_cme = nr_seq_lote_transf_p
	and	ie_tipo_nota = 'SE'
	and	ie_situacao = 1;

	if (nr_seq_nota_fiscal_w > 0) then
		--Não é possível estornar a liberação pois há nota fiscal gerada e ativa para esta transferência!
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(264854);
	end if;

	update	cm_lote_transferencia
	set	dt_liberacao  = NULL,
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where	nr_sequencia = nr_seq_lote_transf_p;

	end;
elsif (ie_transf_receb_p = 'R') then
	begin

	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_seq_nota_fiscal_w
	from	nota_fiscal
	where	nr_seq_transf_cme = nr_seq_lote_transf_p
	and	ie_tipo_nota = 'E'
	and	ie_situacao = 1;

	if (nr_seq_nota_fiscal_w > 0) then
		-- Não é possível estornar a liberação pois há nota fiscal gerada e ativa para esta transferência!
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(264854);
	end if;

	update	cm_lote_transferencia
	set	dt_lib_receb  = NULL,
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where	nr_sequencia = nr_seq_lote_transf_p;

	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
