CREATE OR REPLACE PROCEDURE cm_estorna_retirada_receb ( nr_conjunto_cont_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_retirada_w		integer;
qt_receb_w		integer;
nr_sequencia_w		bigint;
nr_seq_conjunto_w		bigint;
cd_local_estoque_w	smallint;
cd_estabelecimento_w	smallint;


BEGIN

select	count(*)
into STRICT	qt_retirada_w
from	cm_expurgo_retirada
where	nr_conjunto_cont = nr_conjunto_cont_p;

select	count(*)
into STRICT	qt_receb_w
from	cm_expurgo_receb
where	nr_conjunto_cont = nr_conjunto_cont_p;

if (qt_retirada_w > 0) then

	select	nr_sequencia,
		nr_seq_conjunto,
		cd_local_estoque,
		cd_estabelecimento
	into STRICT	nr_sequencia_w,
		nr_seq_conjunto_w,
		cd_local_estoque_w,
		cd_estabelecimento_w
	from	cm_expurgo_retirada
	where	nr_conjunto_cont = nr_conjunto_cont_p;

	CALL cm_gera_expurgo_saldo(nr_seq_conjunto_w, cd_local_estoque_w, 'D', cd_estabelecimento_w, nm_usuario_p);

	update	cm_expurgo_retirada
	set	dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p,
		cd_local_estoque  = NULL,
		dt_retirada  = NULL,
		nm_usuario_retirada  = NULL,
		nr_seq_interno  = NULL,
		cd_setor_retirada  = NULL
	where	nr_sequencia = nr_sequencia_w;

elsif (qt_receb_w > 0) then

	select	nr_sequencia,
		nr_seq_conjunto,
		cd_local_estoque,
		cd_estabelecimento
	into STRICT	nr_sequencia_w,
		nr_seq_conjunto_w,
		cd_local_estoque_w,
		cd_estabelecimento_w
	from	cm_expurgo_receb
	where	nr_conjunto_cont = nr_conjunto_cont_p;

	CALL cm_gera_expurgo_saldo(nr_seq_conjunto_w, cd_local_estoque_w, 'D', cd_estabelecimento_w, nm_usuario_p);

	update	cm_expurgo_receb
	set	dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p,
		nm_usuario_receb  = NULL,
		cd_setor_receb  = NULL,
		nm_usuario_entrega  = NULL,
		cd_local_estoque  = NULL,
		dt_recebimneto  = NULL,
		ds_observacao  = NULL
	where	nr_sequencia = nr_sequencia_w;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
