CREATE OR REPLACE PROCEDURE cm_exclui_conj_expurgo ( nr_conjunto_cont_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE



qt_conj_retirada_w	integer;
qt_conj_receb_w		integer;
qt_item_cont_w		integer;
qt_conj_cont_w		integer;
qt_conj_movto_w		integer;


BEGIN

select	count(*)
into STRICT	qt_conj_retirada_w
from	cm_expurgo_retirada
where	nr_conjunto_cont = nr_conjunto_cont_p;

select	count(*)
into STRICT	qt_item_cont_w
from	cm_item_cont
where	nr_seq_conjunto = nr_conjunto_cont_p;

select	count(*)
into STRICT	qt_conj_movto_w
from	cm_conjunto_movto
where	nr_seq_conjunto = nr_conjunto_cont_p;

select	count(*)
into STRICT	qt_conj_cont_w
from	cm_conjunto_cont
where	nr_sequencia = nr_conjunto_cont_p;

select	count(*)
into STRICT	qt_conj_receb_w
from	cm_expurgo_receb
where	nr_conjunto_cont = nr_conjunto_cont_p;

if (qt_conj_receb_w > 0) then

	delete	FROM cm_expurgo_receb
	where	nr_conjunto_cont = nr_conjunto_cont_p;

end if;

if (qt_conj_retirada_w > 0) then

	delete	FROM cm_expurgo_retirada
	where	nr_conjunto_cont = nr_conjunto_cont_p;

end if;

if (qt_item_cont_w > 0) then

	delete	FROM cm_item_cont
	where	nr_seq_conjunto = nr_conjunto_cont_p;

end if;

if (qt_conj_movto_w > 0) then

	delete	FROM cm_conjunto_movto
	where	nr_seq_conjunto = nr_conjunto_cont_p;

end if;

if (qt_conj_cont_w > 0) then

	delete	FROM cm_conjunto_cont
	where	nr_sequencia = nr_conjunto_cont_p;

end if;

commit;

end;
