SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE cm_gerar_codigo_item_conj ( nr_seq_conjunto_p bigint, cd_estabelecimento_p bigint, nr_seq_interno_p bigint, ie_forma_codigo_p text, nm_usuario_p text, cd_codigo_item_p INOUT text) AS $body$
DECLARE


cd_codigo_item_w		varchar(255);
ds_estabelecimento_w		varchar(255);
ds_reduzida_w			varchar(255);
nr_seq_interno_w		bigint;
ds_seq_interno_item_w		varchar(10);
ds_seq_interno_conj_w		varchar(10);
ie_letra_w			varchar(10);


BEGIN

if (ie_forma_codigo_p in ('1','2','3','4','5')) then
	begin

	if (ie_forma_codigo_p = '1') then
		begin

		select	substr(nm_fantasia_estab,1,1)
		into STRICT	ds_estabelecimento_w
		from	estabelecimento_v
		where	cd_estabelecimento = cd_estabelecimento_p;

		end;

	elsif (ie_forma_codigo_p = '2') then
		ds_estabelecimento_w	:= 'A';

	elsif (ie_forma_codigo_p = '3') then
		ds_estabelecimento_w	:= 'I';

	elsif (ie_forma_codigo_p = '4') then
		ds_estabelecimento_w	:= 'M';

	elsif (ie_forma_codigo_p = '5') then
		ie_letra_w := obter_param_usuario(406, 191, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_letra_w);
		ds_estabelecimento_w	:= ie_letra_w;

	end if;


	select	substr(ds_reduzida,1,100),
		coalesce(nr_seq_interno,0)
	into STRICT	ds_reduzida_w,
		nr_seq_interno_w
	from	cm_conjunto
	where	nr_sequencia = nr_seq_conjunto_p;

	select	adiciona_zeros_esquerda(nr_seq_interno_w,2)
	into STRICT	ds_seq_interno_conj_w
	;

	nr_seq_interno_w := coalesce(nr_seq_interno_p,0);

	select	adiciona_zeros_esquerda(nr_seq_interno_w,3)
	into STRICT	ds_seq_interno_item_w
	;

	cd_codigo_item_w := substr(ds_estabelecimento_w || ds_reduzida_w || ds_seq_interno_conj_w || ds_seq_interno_item_w || '.01' ,1,100);

	end;
end if;

cd_codigo_item_p := cd_codigo_item_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
