CREATE OR REPLACE PROCEDURE cm_gerar_cod_ext_itens ( nr_seq_conjunto_p bigint, nm_usuario_p text, cd_estabelecimento_p text) AS $body$
DECLARE



nr_seq_item_w		bigint;
nr_seq_interno_w	bigint;
ie_forma_codigo_w	varchar(255);
cd_codigo_item_w	varchar(255);

C01 CURSOR FOR
	SELECT	nr_seq_item,
		nr_seq_interno
	from 	cm_conjunto_item
	where 	nr_seq_conjunto = nr_seq_conjunto_p;


BEGIN
ie_forma_codigo_w := obter_param_usuario(406, 61, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_forma_codigo_w);


open C01;
loop
fetch C01 into
	nr_seq_item_w,
	nr_seq_interno_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (coalesce(nr_seq_item_w,0) > 0) then

		cd_codigo_item_w := cm_gerar_codigo_item_conj(nr_seq_conjunto_p, cd_estabelecimento_p, nr_seq_interno_w, ie_forma_codigo_w, nm_usuario_p, cd_codigo_item_w);

		update 	cm_conjunto_item
		set 	cd_codigo = cd_codigo_item_w
		where	nr_seq_conjunto = nr_seq_conjunto_p
		and 	nr_seq_item = nr_seq_item_w
		and 	nr_seq_interno = nr_seq_interno_w;

	end if;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
