CREATE OR REPLACE PROCEDURE cm_gerar_receb_item_expurgo ( nr_seq_item_p text, cd_setor_atendimento_p bigint, cd_estabelecimento_p bigint, qt_item_p bigint, nm_usuario_p text, ds_erro_p INOUT text) AS $body$
DECLARE


ds_erro_w		varchar(2000) := '';
qt_existe_w		integer;
nr_sequencia_w	bigint;
qt_item_w		bigint;
ie_barras_w		varchar(10) := '';
nr_seq_embalagem_w cm_item.nr_seq_embalagem%type;
cd_codigo_w		varchar(10);


BEGIN

qt_item_w := qt_item_p;

select coalesce(max(nr_seq_embalagem),0)
into STRICT   nr_seq_embalagem_w
from   cm_item
where  nr_sequencia = nr_seq_item_p;

SELECT * FROM cm_converte_cod_barras(nr_seq_item_p, cd_codigo_w, ds_erro_w, 'I') INTO STRICT cd_codigo_w, ds_erro_w;

if (nr_seq_embalagem_w = 0) then
ds_erro_w	:= WHEB_MENSAGEM_PCK.get_texto(1045942);
end if;

if (coalesce(ds_erro_w::text, '') = '') then
	
	while(qt_item_w > 0) loop
		begin
		
		select	nextval('cm_expurgo_receb_seq')
		into STRICT	nr_sequencia_w
		;
		
		insert into cm_expurgo_receb(
			nr_sequencia,
			cd_estabelecimento,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			ie_status,
			nr_seq_item)
		values (	nr_sequencia_w,
			cd_estabelecimento_p,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			2,
			cd_codigo_w);

		CALL cm_gerar_item_cont_expurgo(cd_codigo_w,null,null,null,nr_sequencia_w,cd_setor_atendimento_p,nm_usuario_p,cd_estabelecimento_p);
	
		qt_item_w := (qt_item_w - 1);

		end;
	end loop;

end if;

ds_erro_p := ds_erro_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
