SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE cm_gerar_retorno_item_envio ( nr_seq_envio_p bigint, nr_seq_item_p bigint, ds_lote_p text, dt_validade_p timestamp, qt_conjunto_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_item_envio_w		bigint;
qt_pendente_w			bigint;
qt_conjunto_w			bigint;
qt_envio_item_w			double precision;


BEGIN

select	coalesce(min(nr_sequencia),0)
into STRICT	nr_seq_item_envio_w
from	cm_envio_externo_item
where	nr_seq_envio = nr_seq_envio_p
and (nr_seq_item = nr_seq_item_p or nr_seq_conjunto = nr_seq_item_p)
and	coalesce(dt_retorno::text, '') = '';


if (nr_seq_item_envio_w > 0) then
	begin

	select	coalesce(sum(qt_envio_item),0)
	into STRICT	qt_envio_item_w
	from	cm_envio_externo_item
	where	nr_seq_envio = nr_seq_envio_p
	and (nr_seq_item = nr_seq_item_p or nr_seq_conjunto = nr_seq_item_p)
	and     nr_sequencia = nr_seq_item_envio_w
	and	coalesce(dt_retorno::text, '') = '';



	if (coalesce(qt_conjunto_p,qt_envio_item_w) < qt_envio_item_w) then

		insert into cm_envio_externo_item(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_seq_envio,
			nr_seq_item,
			ds_lote,
			dt_validade,
			nr_seq_lote,
			cd_codigo,
			dt_retorno,
			nr_seq_nota,
			qt_envio_item,
			nr_seq_conjunto,
			ds_observacao)
		SELECT	nextval('cm_envio_externo_item_seq'),
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_envio_p,
			nr_seq_item,
			ds_lote,
			dt_validade,
			nr_seq_lote,
			cd_codigo,
			null,
			nr_seq_nota,
			(qt_envio_item_w - coalesce(qt_conjunto_p,0)),
			nr_seq_conjunto,
			ds_observacao
		from	cm_envio_externo_item
		where	nr_seq_envio = nr_seq_envio_p
		and	nr_sequencia = nr_seq_item_envio_w;

	end if;

	update	cm_envio_externo_item
	set	dt_retorno = clock_timestamp(),
		ds_lote = CASE WHEN ds_lote_p = NULL THEN ds_lote  ELSE ds_lote_p END ,
		dt_validade = CASE WHEN dt_validade_p = NULL THEN dt_validade  ELSE dt_validade_p END ,
		qt_envio_item = coalesce(qt_conjunto_p, qt_envio_item)
	where	nr_seq_envio = nr_seq_envio_p
	and	nr_sequencia = nr_seq_item_envio_w;

	end;
end if;

select	count(*)
into STRICT	qt_pendente_w
from	cm_envio_externo_item
where	nr_seq_envio = nr_seq_envio_p
and	coalesce(dt_retorno::text, '') = '';

if (qt_pendente_w = 0) then

	update	cm_envio_externo
	set	dt_retorno = clock_timestamp()
	where	nr_sequencia = nr_seq_envio_p;

end if;

select	count(*)
into STRICT	qt_conjunto_w
from	cm_envio_externo_item
where	nr_sequencia = nr_seq_item_envio_w
and	(nr_seq_conjunto IS NOT NULL AND nr_seq_conjunto::text <> '');

if (qt_conjunto_w > 0) and (nr_seq_item_envio_w > 0)	then
	CALL cm_gerar_conj_ester_envio_ext(nr_seq_envio_p, nr_seq_item_envio_w, cd_estabelecimento_p, nm_usuario_p);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
