CREATE OR REPLACE PROCEDURE cm_gera_conj_item_avulso (nr_seq_classif_conj_p bigint, nr_seq_classif_item_p bigint, nr_seq_embalagem_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


nm_item_w		varchar(255);
nr_seq_item_w		bigint;
nr_seq_conjunto_w	bigint;

c01 CURSOR FOR
SELECT	nr_sequencia,
	nm_item
from	cm_item a
where	nr_seq_classif = nr_seq_classif_item_p
and	ie_situacao = 'A'
and	not exists (select	1
		from	cm_conjunto_item x,
			cm_conjunto y
		where	x.nr_seq_conjunto = y.nr_sequencia
		and	x.nr_seq_item = a.nr_sequencia
		and	y.nr_seq_classif = nr_seq_classif_conj_p);


BEGIN

open c01;
loop
fetch c01 into
	nr_seq_item_w,
	nm_item_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	select	nextval('cm_conjunto_seq')
	into STRICT	nr_seq_conjunto_w
	;

	insert into cm_conjunto(
		nr_sequencia,
		nm_conjunto,
		nr_seq_classif,
		nr_seq_embalagem,
		qt_ponto,
		cd_estabelecimento,
		ie_situacao,
		nm_usuario,
		dt_atualizacao,
		ie_gerar_conj_auto,
		ie_agendamento)
	values (	nr_seq_conjunto_w,
		nm_item_w,
		nr_seq_classif_conj_p,
		nr_seq_embalagem_p,
		1,
		cd_estabelecimento_p,
		'A',
		nm_usuario_p,
		clock_timestamp(),
		'S',
		'S');

	insert into cm_conjunto_item(
		nr_seq_conjunto,
		nr_seq_item,
		qt_item,
		dt_atualizacao,
		nm_usuario,
		ie_indispensavel)
	values (	nr_seq_conjunto_w,
		nr_seq_item_w,
		1,
		clock_timestamp(),
		nm_usuario_p,
		'N');

	commit;

	end;
end loop;
close c01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
