CREATE OR REPLACE PROCEDURE cm_gera_expurgo_saldo ( nr_seq_conjunto_p bigint, cd_local_estoque_p bigint, ie_opcao_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_existe_w		integer;
nr_sequencia_w		bigint;

/*
ie_opcao_p
A = Soma;
D = Subtrai;
*/
BEGIN

select	count(*)
into STRICT	qt_existe_w
from	cm_expurgo_saldo
where	cd_estabelecimento = cd_estabelecimento_p
and	nr_seq_conjunto = nr_seq_conjunto_p
and	cd_local_estoque = cd_local_estoque_p;

if (qt_existe_w > 0) then

	select	max(nr_sequencia)
	into STRICT	nr_sequencia_w
	from	cm_expurgo_saldo
	where	cd_estabelecimento = cd_estabelecimento_p
	and	nr_seq_conjunto = nr_seq_conjunto_p
	and	cd_local_estoque = cd_local_estoque_p;

	if (ie_opcao_p = 'A') then

		update	cm_expurgo_saldo
		set	qt_saldo = qt_saldo + 1,
			dt_atualizacao = clock_timestamp(),
			nm_usuario = nm_usuario_p
		where	nr_sequencia = nr_sequencia_w;

	elsif (ie_opcao_p = 'D') then

		update	cm_expurgo_saldo
		set	qt_saldo = qt_saldo - 1,
			dt_atualizacao = clock_timestamp(),
			nm_usuario = nm_usuario_p
		where	nr_sequencia = nr_sequencia_w;

	end if;

elsif (ie_opcao_p <> 'D') then

	insert into cm_expurgo_saldo(
		nr_sequencia,
		cd_estabelecimento,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_conjunto,
		qt_saldo,
		cd_local_estoque)
	values (	nextval('cm_expurgo_saldo_seq'),
		cd_estabelecimento_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_conjunto_p,
		1,
		cd_local_estoque_p);

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
