CREATE OR REPLACE PROCEDURE cm_grava_conj_consistido ( nr_seq_conjunto_p bigint, nr_seq_item_p bigint, nr_seq_transferencia_p bigint, ie_forma_consiste_p text, ie_transf_receb_p text, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w		bigint;


BEGIN

select	coalesce(max(nr_sequencia),0)
into STRICT	nr_sequencia_w
from	cm_lote_transf_conj
where	nr_seq_conjunto = nr_seq_conjunto_p
and	nr_seq_lote_transf = nr_seq_transferencia_p;

if (nr_sequencia_w > 0) then

	if (ie_transf_receb_p = 'T') then

		update	cm_lote_transf_conj
		set	ie_consistido_transf = 'S'
		where	nr_sequencia = nr_sequencia_w;

	else

		update	cm_lote_transf_conj
		set	ie_consistido_receb = 'S'
		where	nr_sequencia = nr_sequencia_w;

	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
