SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE cm_insere_conj_esterilizacao ( nr_seq_conjunto_p bigint, nr_seq_ciclo_p bigint, nr_seq_lote_p bigint, cd_setor_atendimento_p bigint, nm_usuario_barras_p text, cd_estabelecimento_p bigint, nr_seq_ciclo_lav_p bigint, ds_erro_p INOUT bigint) AS $body$
DECLARE


nr_seq_ciclo_w       		cm_conjunto_cont.nr_seq_ciclo%type;
nr_seq_lote_w        		cm_conjunto_cont.nr_seq_lote%type;
nr_seq_embalagem_w   	cm_conjunto.nr_seq_embalagem%type;
qt_ponto_w           		cm_conjunto.qt_ponto%type;
nr_sequencia_w       	cm_conjunto_cont.nr_sequencia%type;
nr_seq_controle_w    	cm_conjunto_cont.nr_seq_controle%type;
cd_fornecedor_w       	cm_conjunto.cd_fornecedor%type;
qt_existe_validade_w 	cm_classif_embalagem.qt_dia_validade%type;
dt_validade_w        		timestamp;


BEGIN

nr_seq_ciclo_w		:= coalesce(nr_seq_ciclo_p,0);
nr_seq_lote_w		:= coalesce(nr_seq_lote_p,0);

select	nr_seq_embalagem,
	qt_ponto,
	cd_fornecedor
into STRICT	nr_seq_embalagem_w,
	qt_ponto_w,
	cd_fornecedor_w
from	cm_conjunto
where	nr_sequencia = nr_seq_conjunto_p;

select	nextval('cm_conjunto_cont_seq')
into STRICT	nr_sequencia_w
;

if (nr_seq_lote_w = 0) then
	begin
	select	coalesce(max(nr_seq_controle),0) + 1
	into STRICT	nr_seq_controle_w
	from	cm_conjunto_cont;
	EXCEPTION
		WHEN OTHERS THEN
      			ds_erro_p := nr_sequencia_w;
      			nr_seq_controle_w := NULL;
	end;
else
	begin
	select	nr_seq_controle
	into STRICT	nr_seq_controle_w
	from	cm_lote_conjunto
	where	nr_sequencia = nr_seq_lote_w;
	EXCEPTION
		WHEN OTHERS THEN
      			ds_erro_p := nr_sequencia_w;
      			nr_seq_controle_w := NULL;
	end;
end if;

select	coalesce(max(qt_dia_validade),0)
into STRICT	qt_existe_validade_w
from 	cm_classif_embalagem
where	nr_sequencia = nr_seq_embalagem_w;

if (qt_existe_validade_w > 0) then
	select (clock_timestamp() + qt_dia_validade)
	into STRICT	dt_validade_w
	from	cm_classif_embalagem
	where	nr_sequencia = nr_seq_embalagem_w;

end if;
	
insert into cm_conjunto_cont(
	nr_sequencia,
	nr_seq_conjunto,
	cd_estabelecimento,
	dt_atualizacao,
	nm_usuario,
	ie_status_conjunto,
	dt_origem,
	nm_usuario_origem,
	nr_seq_embalagem,
	dt_receb_ester,
	nm_usuario_ester,
	nr_seq_ciclo,
	vl_esterilizacao,
	qt_ponto,
	cd_setor_atendimento,
	nr_seq_lote,
	nr_seq_controle,
	ie_situacao,
	cd_cgc,
	dt_validade,
	nr_seq_ciclo_lav)
values (nr_sequencia_w,
	nr_seq_conjunto_p,
	cd_estabelecimento_p,
	clock_timestamp(),
	nm_usuario_barras_p,
	1,
	clock_timestamp(),
	nm_usuario_barras_p,
	nr_seq_embalagem_w,
	clock_timestamp(),
	nm_usuario_barras_p,
	CASE WHEN nr_seq_ciclo_w=0 THEN null  ELSE nr_seq_ciclo_w END ,
	0,
	qt_ponto_w,
	cd_setor_atendimento_p,
	CASE WHEN nr_seq_lote_w=0 THEN null  ELSE nr_seq_lote_w END ,
	nr_seq_controle_w,
	'A',
	CASE WHEN coalesce(cd_fornecedor_w,0)=0 THEN null  ELSE cd_fornecedor_w END ,
	CASE WHEN coalesce(dt_validade_w,'')='' THEN null  ELSE dt_validade_w END ,
	nr_seq_ciclo_lav_p);

CALL cme_incluir_itens_controle(nr_sequencia_w,nm_usuario_barras_p);
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
