CREATE OR REPLACE PROCEDURE cm_inserir_conj_ciclo_barras ( nr_seq_conjunto_p bigint, nr_seq_ciclo_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_conj_cont_w		bigint;
ie_atualiza_validade_w		varchar(1);
dt_validade_w			timestamp;
nr_seq_equipamento_w		bigint;
nr_seq_embalagem_w		bigint;


BEGIN

ie_atualiza_validade_w := obter_valor_param_usuario(406, 20, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p);

update	cm_conjunto_cont
set	nr_seq_ciclo = nr_seq_ciclo_p,
	nm_usuario = nm_usuario_p,
	dt_atualizacao = clock_timestamp()
where	nr_sequencia = nr_seq_conjunto_p;

if (ie_atualiza_validade_w = 'S') then
	begin

	select	a.nr_seq_equipamento,
		b.nr_seq_embalagem
	into STRICT	nr_seq_equipamento_w,
		nr_seq_embalagem_w
	from	cm_conjunto_cont b,
		cm_ciclo a
	where	a.nr_sequencia = b.nr_seq_ciclo
	and	b.nr_sequencia = nr_seq_conjunto_p;

	select	cm_calcular_validade_conj(nr_seq_equipamento_w,nr_seq_embalagem_w)
	into STRICT	dt_validade_w
	;

	update	cm_conjunto_cont
	set	dt_validade = dt_validade_w,
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where	nr_sequencia = nr_seq_conjunto_p;

	end;
end if;

commit;

end;
$body$
