CREATE OR REPLACE PROCEDURE cm_liberar_ciclo ( nr_seq_ciclo_p bigint, nr_seq_ciclo_lav_p bigint, nr_seq_motivo_p bigint, ie_libera_p text, ds_observacao_p text, nm_usuario_p text) AS $body$
DECLARE


ds_motivo_w		varchar(255);
ds_observacao_w		varchar(2000);
ie_tipo_movto_w		varchar(1);


BEGIN
if (nr_seq_ciclo_p > 0) then
	
	if (ie_libera_p = 'S') then
		update	cm_ciclo
		set	dt_liberacao = clock_timestamp(),
			nm_usuario_lib = nm_usuario_p
		where	nr_sequencia = nr_seq_ciclo_p;
	end if;
	
elsif (nr_seq_ciclo_lav_p > 0) then
	
	if (ie_libera_p = 'S') then
		update	cm_ciclo_lavacao
		set	dt_liberacao = clock_timestamp(),
			nm_usuario_lib = nm_usuario_p
		where	nr_sequencia = nr_seq_ciclo_lav_p;
	end if;
	
end if;

select	max(ds_motivo)
into STRICT	ds_motivo_w
from	cm_motivo_lib_ciclo
where	nr_sequencia = coalesce(nr_seq_motivo_p,0);

ds_observacao_w	:= ds_motivo_w;

if (ds_observacao_p IS NOT NULL AND ds_observacao_p::text <> '') then
	select	CASE WHEN ds_motivo_w='' THEN ds_observacao_p  ELSE ds_motivo_w || ' - ' || ds_observacao_p END
	into STRICT	ds_observacao_w
	;
end if;

ds_observacao_w	:= substr(ds_observacao_w,1,255);

if (ie_libera_p = 'S') then
	ie_tipo_movto_w	:= 'L';
else
	ie_tipo_movto_w	:= 'N';
end if;

CALL cm_atualiza_ciclo_movto(nm_usuario_p,ds_observacao_w,ie_tipo_movto_w,nr_seq_ciclo_p,nr_seq_ciclo_lav_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
