SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE cm_liberar_transferencia_conj ( nr_seq_transferencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_liberado_w		varchar(1);


BEGIN

select	CASE WHEN coalesce(dt_liberacao::text, '') = '' THEN 'N'  ELSE 'S' END
into STRICT	ie_liberado_w
from	cm_lote_transferencia
where	nr_sequencia = nr_seq_transferencia_p;

if (ie_liberado_w = 'S') then
	-- Esta transferência já está liberada!
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(264860);
end if;

update	cm_lote_transferencia
set	dt_liberacao = clock_timestamp(),
	nm_usuario = nm_usuario_p,
	dt_atualizacao = clock_timestamp()
where	nr_sequencia = nr_seq_transferencia_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
