SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE cm_retira_conj_transferencia ( nr_seq_lote_transf_p bigint, nr_seq_conjunto_p bigint) AS $body$
DECLARE


ie_liberado_w		varchar(1);


BEGIN

select	CASE WHEN coalesce(dt_liberacao::text, '') = '' THEN 'N'  ELSE 'S' END
into STRICT	ie_liberado_w
from	cm_lote_transferencia
where	nr_sequencia = nr_seq_lote_transf_p;

if (ie_liberado_w = 'S') then
	--'Esta transferência já está liberada! O conjunto não pode ser retirado.
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(267179);
end if;

delete
from	cm_transf_conj_cb
where	nr_seq_lote_transf = nr_seq_lote_transf_p
and	nr_seq_conjunto = nr_seq_conjunto_p;

delete
from	cm_lote_transf_conj
where	nr_seq_lote_transf = nr_seq_lote_transf_p
and	nr_seq_conjunto = nr_seq_conjunto_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
