SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE cm_retorna_conj_cont_barras ( cd_barras_p text, cd_codigo_p INOUT text, ie_tipo_p INOUT text, nr_requisicao_p bigint) AS $body$
DECLARE


cd_codigo_w				varchar(10);
ds_erro_w				varchar(2000) := '';
nr_seq_conjunto_cont_w	cm_conjunto_cont.nr_sequencia%type;
nr_seq_conjunto_w		cm_conjunto_cont.nr_seq_conjunto%type;
nr_seq_item_w			cm_conjunto_cont.nr_seq_item%type;
nr_seq_classif_w		cm_item.nr_seq_classif%type;
qt_requisitado_w		bigint;


BEGIN

if (cd_barras_p IS NOT NULL AND cd_barras_p::text <> '') then

	SELECT * FROM cm_converte_cod_barras(cd_barras_p, cd_codigo_w, ds_erro_w, 'I') INTO STRICT cd_codigo_w, ds_erro_w;

	ie_tipo_p := 'I';

	if (coalesce(cd_codigo_w::text, '') = '') then
		ie_tipo_p := 'C';
		SELECT * FROM cm_converte_cod_barras(cd_barras_p, cd_codigo_w, ds_erro_w, 'C') INTO STRICT cd_codigo_w, ds_erro_w;
	end if;

	if ((coalesce(ds_erro_w::text, '') = '' or ds_erro_w = '') and (cd_codigo_w IS NOT NULL AND cd_codigo_w::text <> '')) then

		if (ie_tipo_p = 'I') then

			select 	max(nr_seq_item),
					max(nr_sequencia)
			into STRICT	nr_seq_item_w,
					nr_seq_conjunto_cont_w
			from 	cm_conjunto_cont
			where 	ie_status_conjunto = 3
			and 	ie_situacao = 'A'
			and 	nr_seq_item = cd_codigo_w;

		elsif (ie_tipo_p = 'C') then

			select 	max(nr_seq_conjunto),
					max(nr_sequencia)
			into STRICT	nr_seq_conjunto_w,
					nr_seq_conjunto_cont_w
			from 	cm_conjunto_cont
			where 	ie_status_conjunto = 3
			and 	ie_situacao = 'A'
			and 	nr_seq_conjunto = cd_codigo_w;

		end if;

		if (nr_seq_conjunto_w IS NOT NULL AND nr_seq_conjunto_w::text <> '') then

			select 	nr_seq_classif
			into STRICT	nr_seq_classif_w
			from 	cm_conjunto
			where 	nr_sequencia = nr_seq_conjunto_w;

			select 	count(*)
			into STRICT	qt_requisitado_w
			from 	cm_requisicao_item
			where 	nr_seq_requisicao = nr_requisicao_p
			and (nr_seq_conjunto = nr_seq_conjunto_w or nr_seq_classif_conj = nr_seq_classif_w);

			if (qt_requisitado_w > 0) then
				cd_codigo_p := nr_seq_conjunto_cont_w;
			end if;

		elsif (nr_seq_item_w IS NOT NULL AND nr_seq_item_w::text <> '') then

			select 	nr_seq_classif
			into STRICT	nr_seq_classif_w
			from 	cm_item
			where 	nr_sequencia = nr_seq_item_w;

			select 	count(*)
			into STRICT	qt_requisitado_w
			from 	cm_requisicao_item
			where 	nr_seq_requisicao = nr_requisicao_p
			and 	nr_seq_classif = nr_seq_classif_w;

			if (qt_requisitado_w > 0) then
				cd_codigo_p := nr_seq_conjunto_cont_w;
			end if;

		end if;
	end if;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
