SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE cm_vincula_conj_cirurgia_req ( nr_seq_conj_cont_p bigint, nr_cirurgia_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_agenda_w			cm_requisicao.nr_seq_agenda%type;
nr_seq_requisicao_w		bigint;
nr_seq_conjunto_w		bigint;
nr_sequencia_w			bigint;


BEGIN

select	coalesce(max(nr_sequencia),0)
into STRICT	nr_seq_agenda_w
from	agenda_paciente
where	nr_cirurgia = nr_cirurgia_p;

if (nr_seq_agenda_w <> 0) then
	begin

	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_seq_requisicao_w
	from	cm_requisicao
	where	nr_seq_agenda = nr_seq_agenda_w;

	if (nr_seq_requisicao_w <> 0) then
		begin

		select	nr_seq_conjunto
		into STRICT	nr_seq_conjunto_w
		from	cm_conjunto_cont
		where	nr_sequencia = nr_seq_conj_cont_p
		and	coalesce(ie_situacao,'A') = 'A';

		select	nextval('cm_requisicao_item_seq')
		into STRICT	nr_sequencia_w
		;

		insert into cm_requisicao_item(
			nr_sequencia,
			nr_seq_requisicao,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_seq_conjunto,
			qt_conjunto,
			cd_motivo_baixa,
			nm_usuario_atend,
			ie_devolucao,
			ie_emprestimo,
			ds_observacao) values (
				nr_sequencia_w,
				nr_seq_requisicao_w,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				nr_seq_conjunto_w,
				1,
				null,
				null,
				'N',
				'N',

				WHEB_MENSAGEM_PCK.get_texto(457521,'nr_cirurgia='||nr_cirurgia_p));

		CALL cme_baixa_req_barras(nr_seq_requisicao_w,nr_seq_conj_cont_p,nm_usuario_p,1);			

		end;
	end if;

	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
