CREATE OR REPLACE PROCEDURE cm_vinc_atende_conj_agenda ( nr_seq_requisicao_p bigint, nr_seq_conjunto_cont_p bigint default 0, nr_seq_agenda_p bigint DEFAULT NULL, nm_usuario_p text DEFAULT NULL, ie_vincular_agenda_p text default 'S', nr_seq_item_cont_p bigint default 0) AS $body$
DECLARE


qt_existe_w		bigint;
nr_cirurgia_w		bigint;
dt_validade_w		timestamp;
nr_atendimento_w	bigint;
nr_seq_conjunto_w	bigint;
nr_seq_agenda_cme_w	bigint;
nr_seq_item_req_w	bigint;
cd_estabelecimento_w	estabelecimento.cd_estabelecimento%type;
nr_seq_classif_w 	bigint;
nr_seq_item_w	cm_conjunto_cont.nr_seq_item%type;



BEGIN
cd_estabelecimento_w := coalesce(wheb_usuario_pck.get_cd_estabelecimento,1);

	
if ((nr_seq_conjunto_cont_p IS NOT NULL AND nr_seq_conjunto_cont_p::text <> '') or (nr_seq_item_cont_p IS NOT NULL AND nr_seq_item_cont_p::text <> '')) then

	if (nr_seq_conjunto_cont_p IS NOT NULL AND nr_seq_conjunto_cont_p::text <> '')then
	select	count(*)
		into STRICT	qt_existe_w
		from	cm_conjunto_cont
		where	nr_sequencia = nr_seq_conjunto_cont_p
		and 	cd_estabelecimento = cd_estabelecimento_w
		and 	(nr_seq_conjunto IS NOT NULL AND nr_seq_conjunto::text <> '')
		and	coalesce(ie_situacao,'A') = 'A';
			if (qt_existe_w = 0) then
				CALL WHEB_MENSAGEM_PCK.Exibir_Mensagem_Abort(150070);
			end if;
	end if;		
	
	if (nr_seq_item_cont_p IS NOT NULL AND nr_seq_item_cont_p::text <> '')then
	select	count(*)
		into STRICT	qt_existe_w
		from	cm_conjunto_cont
		where	nr_sequencia = nr_seq_item_cont_p
		and 	cd_estabelecimento = cd_estabelecimento_w
		and 	(nr_seq_item IS NOT NULL AND nr_seq_item::text <> '')
		and	coalesce(ie_situacao,'A') = 'A';
			if (qt_existe_w = 0) then
				CALL WHEB_MENSAGEM_PCK.Exibir_Mensagem_Abort(150070);
			end if;
	end if;		

	select	count(*)
	into STRICT	qt_existe_w
	from	cm_requisicao
	where	nr_sequencia = nr_seq_requisicao_p;

	if (qt_existe_w = 0) then
		CALL WHEB_MENSAGEM_PCK.Exibir_Mensagem_Abort(266320);
	end if;

	select	count(*)
	into STRICT	qt_existe_w
	from	cm_requisicao_conj
	where	nr_seq_conj_real = coalesce(nr_seq_conjunto_cont_p, nr_seq_item_cont_p);

	if (qt_existe_w > 0) then
		CALL WHEB_MENSAGEM_PCK.Exibir_Mensagem_Abort(266319);
	end if;

	select	count(*)
	into STRICT	qt_existe_w
	from	cm_conjunto_cont
	where	nr_sequencia = coalesce(nr_seq_conjunto_cont_p, nr_seq_item_cont_p)
	and	ie_status_conjunto = 3
	and	coalesce(ie_situacao,'A') = 'A';

	if (qt_existe_w = 0) then
		CALL WHEB_MENSAGEM_PCK.Exibir_Mensagem_Abort(266318);
	end if;

	select	nr_cirurgia,
		dt_validade
	into STRICT	nr_cirurgia_w,
		dt_validade_w
	from	cm_conjunto_cont
	where	nr_sequencia = coalesce(nr_seq_conjunto_cont_p, nr_seq_item_cont_p)
	and	coalesce(ie_situacao,'A') = 'A';

	if (trunc(coalesce(dt_validade_w,clock_timestamp())) < trunc(clock_timestamp())) then
		CALL WHEB_MENSAGEM_PCK.Exibir_Mensagem_Abort(266317);
	end if;

	if (nr_cirurgia_w IS NOT NULL AND nr_cirurgia_w::text <> '') then
		select	nr_atendimento
		into STRICT	nr_atendimento_w
		from	cirurgia
		where	nr_cirurgia = nr_cirurgia_w;
		CALL wheb_mensagem_pck.exibir_mensagem_abort(266316,'NR_CIRURGIA_W='||NR_CIRURGIA_W||';NR_ATENDIMENTO_W='||NR_ATENDIMENTO_W);
	end if;

	if ((nr_seq_conjunto_cont_p IS NOT NULL AND nr_seq_conjunto_cont_p::text <> '') and coalesce(ie_vincular_agenda_p,'S') = 'S') then
	
		select	nr_seq_conjunto
		into STRICT	nr_seq_conjunto_w
		from	cm_conjunto_cont
		where	nr_sequencia = nr_seq_conjunto_cont_p
		and	coalesce(ie_situacao,'A') = 'A';
		
		select	nextval('agenda_pac_cme_seq')
		into STRICT	nr_seq_agenda_cme_w
		;

		insert into agenda_pac_cme(
			nr_sequencia,
			nr_seq_agenda,
			dt_atualizacao,
			nm_usuario,
			nr_seq_conjunto,
			qt_conjunto,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			ie_origem_inf,
			ie_obrigatorio) values (
				nr_seq_agenda_cme_w,
				nr_seq_agenda_p,
				clock_timestamp(),
				nm_usuario_p,
				nr_seq_conjunto_w,
				1,
				clock_timestamp(),
				nm_usuario_p,
				'I',
				'N');

		select	nextval('cm_requisicao_item_seq')
		into STRICT	nr_seq_item_req_w
		;

		insert into cm_requisicao_item(
			nr_sequencia,
			nr_seq_requisicao,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_seq_conjunto,
			qt_conjunto,
			ie_devolucao,
			ie_emprestimo,
			ds_observacao) values (
				nr_seq_item_req_w,
				nr_seq_requisicao_p,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				nr_seq_conjunto_w,
				1, 'N', 'N',
				wheb_mensagem_pck.get_texto(799525,'nr_seq_agenda_p=' || nr_seq_agenda_p));

		CALL cme_baixa_req_barras(nr_seq_requisicao_p,nr_seq_conjunto_cont_p,nm_usuario_p,1);

	end if;

	if ((nr_seq_item_cont_p IS NOT NULL AND nr_seq_item_cont_p::text <> '') and coalesce(ie_vincular_agenda_p,'S') = 'S') then
		
			select	nr_seq_item
			into STRICT	nr_seq_item_w
			from	cm_conjunto_cont
			where nr_sequencia =	nr_seq_item_cont_p
			and	coalesce(ie_situacao,'A') = 'A';		
	
	
			select	nr_seq_classif
			into STRICT	nr_seq_classif_w
			from	cm_item
			where	nr_sequencia = nr_seq_item_w
			and	coalesce(ie_situacao,'A') = 'A';
			
	
		select	nextval('agenda_pac_cme_seq')
			into STRICT	nr_seq_agenda_cme_w
			;
			
	
		insert into agenda_pac_cme(
			nr_sequencia,
			nr_seq_agenda,
			dt_atualizacao,
			nm_usuario,
			nr_seq_classif,
			qt_conjunto,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			ie_origem_inf,
			ie_obrigatorio) values (
				nr_seq_agenda_cme_w,
				nr_seq_agenda_p,
				clock_timestamp(),
				nm_usuario_p,
				nr_seq_classif_w,
				1,
				clock_timestamp(),
				nm_usuario_p,
				'I',
				'N');

		select	nextval('cm_requisicao_item_seq')
		into STRICT	nr_seq_item_req_w
		;

			
		insert into cm_requisicao_item(
			nr_sequencia,
			nr_seq_requisicao,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_seq_classif,
			qt_conjunto,
			ie_devolucao,
			ie_emprestimo,
			ds_observacao) values (
				nr_seq_item_req_w,
				nr_seq_requisicao_p,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				nr_seq_classif_w,
				1,
				'N', 
				'N',
				wheb_mensagem_pck.get_texto(799525,'nr_seq_agenda_p=' || nr_seq_agenda_p));
				CALL cme_baixa_req_barras(nr_seq_requisicao_p,nr_seq_item_cont_p,nm_usuario_p,1);
	end if;
end if;		
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE cm_vinc_atende_conj_agenda ( nr_seq_requisicao_p bigint, nr_seq_conjunto_cont_p bigint default 0, nr_seq_agenda_p bigint DEFAULT NULL, nm_usuario_p text DEFAULT NULL, ie_vincular_agenda_p text default 'S', nr_seq_item_cont_p bigint default 0) FROM PUBLIC;

